insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('1986','V/v báo cáo tình hình tổ chức và hoạt động của HĐND cấp xã','v-v-bao-cao-tinh-hinh-to-chuc-va-hoat-dong-cua-hdnd-cap-xa','','<table style=\"width: 492px; height: 144px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"241\">\r\n<p align=\"center\"><strong>HỘI ĐỒNG NHÂN DÂN</strong></p>\r\n<p align=\"center\"><strong>HUYỆN MỘC CHÂU</strong></p>\r\n<p align=\"center\"><strong>________</strong></p>\r\n<p align=\"center\"><strong>  </strong></p>\r\n<p align=\"center\">Số: <strong>105</strong>/HĐND-VP</p>\r\n<p align=\"center\">V/v báo cáo tình hình tổ chức và hoạt động của HĐND cấp xã</p>\r\n</td>\r\n<td valign=\"top\" width=\"390\">\r\n<p align=\"center\"><strong>CỘNG HÒA XÃ HỘI CHỦ NGHĨA VIỆT NAM</strong></p>\r\n<p align=\"center\"><strong>Độc lập - Tự do - Hạnh phúc</strong></p>\r\n<p align=\"center\"><strong>_______________</strong></p>\r\n<p align=\"center\"><strong> </strong></p>\r\n<p align=\"center\"><em>Mộc Châu, ngày 23 tháng 7 năm 2015</em></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p> </p>\r\n<p>                                  Kính gửi: Thường trực HĐND các xã, thị trấn.</p>\r\n<p>          Thực hiện Công văn số 1764/HĐND ngày 15/7/2015 của Thường trực HĐND tỉnh về báo cáo tình hình tổ chức và hoạt động của HĐND cấp huyện, xã.</p>\r\n<p>Thường trực HĐND huyện đề nghị Thường trực HĐND các xã, thị trấn xây dựng báo cáo tình hình tổ chức và hoạt động của HĐND cấp xã, nhiệm kỳ 2011-2016, như sau:</p>\r\n<p>          <strong>1. Nội dung báo cáo:</strong> Có mẫu đề cương kèm theo: <a href=\"https://www.fshare.vn/file/9BG2OYKL12ZG\"><span style=\"font-size: small; color: #0000ff;\"><strong>Tải về tại đây</strong></span></a></p>\r\n<p>          <strong>2. Mốc thời gian báo cáo:</strong> Từ đầu nhiệm kỳ đến ngày 01/7/2015.</p>\r\n<p>          <strong>3. Thời gian gửi báo cáo:</strong> Thường trực HĐND các xã, thị trấn gửi báo cáo về Thường trực HĐND huyện qua Văn phòng HĐND&amp;UBND huyện, đồng thời gửi 01 bản qua địa chỉ <em><a href=\"mailto:dinhhungmc@gmail.com\">dinhhungmc@gmail.com</a></em> để tổng hợp <strong>trước ngày 27/8/2015.</strong></p>\r\n<p>          Thường trực HĐND huyện đề nghị Thường trực HĐND các xã, thị trấn tập trung triển khai thực hiện đúng tiến độ <em>(Khi cần liên hệ đồng chí Đinh Như Hùng - Chuyên viên Văn phòng HĐND&amp;UBND huyện, SĐT 0945.59.8889)</em>./.</p>\r\n<table style=\"width: 495px; height: 154px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"310\">\r\n<p><strong><em>Nơi nhận:</em></strong></p>\r\n<p>- Như trên;</p>\r\n<p>- Thường trực huyện uỷ;</p>\r\n<p>- TT HĐND huyện;</p>\r\n<p>- Ban Pháp chế HĐND huyện;</p>\r\n<p>- Ban KT-XH HĐND huyện;</p>\r\n<p>- Lưu: VT, Hùng, 25 bản.</p>\r\n</td>\r\n<td valign=\"top\" width=\"310\">\r\n<p align=\"center\"><strong>TM. THƯỜNG TRỰC HĐND</strong></p>\r\n<p align=\"center\"><strong>KT. CHỦ TỊCH</strong></p>\r\n<p align=\"center\"><strong>PHÓ CHỦ TỊCH</strong></p>\r\n<p align=\"center\"><strong> </strong></p>\r\n<p align=\"center\"><strong> </strong></p>\r\n<p> </p>\r\n<p align=\"center\"> <strong>Nguyễn Thị Bích Hoà</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p> <img src=\"images/Anh_tin_bai_2015/Thang_7/28 7 2015 QC.jpg\" border=\"0\" alt=\"\" /></p>','','0','0','0','13','2015-07-28 08:56:40','196','','2015-07-28 08:56:50','196','196','2015-07-28 08:56:44','0000-00-00 00:00:00','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','19','','','1','0','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','0','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('1987','Mộc Châu chuẩn bị tổ chức Ngày hội Văn hóa các dân tộc Mộc Châu năm 2015','moc-chau-chuan-bi-to-chuc-ngay-hoi-van-hoa-cac-dan-toc-moc-chau-nam-2015','','<p style=\"text-align: justify;\">Đã trở thành hoạt động thường niên, Ngày hội Văn hóa các dân tộc huyện Mộc Châu năm 2015 sẽ tiếp tục được tổ chức từ ngày 28 đến ngày 2/9/2015. Tại thời điểm này, UBND huyện Mộc Châu đã ban hành chính thức Kế hoạch 1042/KH - UBND ngày 27/7/2015 về việc tổ chức Ngày hội văn hóa các dân tộc năm 2015 và triển khai thực hiện nhiều hoạt động để chuẩn bị cho Ngày hội.</p>\r\n','\r\n<p style=\"text-align: justify;\">Đã trở thành hoạt động thường niên, Ngày hội Văn hóa các dân tộc huyện Mộc Châu năm 2015 sẽ tiếp tục được tổ chức từ ngày 28 đến ngày 2/9/2015. Tại thời điểm này, UBND huyện Mộc Châu đã ban hành chính thức Kế hoạch 1042/KH - UBND ngày 27/7/2015 về việc tổ chức Ngày hội văn hóa các dân tộc năm 2015 và triển khai thực hiện nhiều hoạt động để chuẩn bị cho Ngày hội.</p>\r\n<p style=\"text-align: justify;\">Ngày hội Văn hóa các dân tộc huyện Mộc Châu năm 2015 sẽ bao gồm các hoạt động chính: Thể thao dân tộc, trò chơi dân gian (thi đẩy gậy, đánh tu lu, rồng ấp trứng, vật gậy, đánh yến,…) ; Hoạt động văn hóa cộng đồng đường phố;, Thi trại văn hoá, văn hoá cộng đồng các dân tộc Mộc Châu; Hội thi ẩm thực các dân tộc Mộc Châu (thi giã bánh dày, thi nấu cơm, trình bày, giới thiệu ẩm thực dân tộc); Trình diễn Lễ hội Hết chá; Trưng bày, triển lãm bản sắc văn hóa các dân tộc Mộc Châu; Hội chợ thương mại; Hội hoa du lịch Mộc Châu; Hoạt động văn hoá, thể thao, du lịch tại khu vực Cửa khẩu Lóng Sập; Chương trình nghệ thuật đêm 1/9, bắn pháo bông chào mừng 70 năm Cách mạng Tháng Tám và Quốc khánh nước CHXHCN Việt Nam.</p>\r\n<p style=\"text-align: justify;\">Điểm mới trong các hoạt động tại Ngày hội Văn hóa các dân tộc huyện Mộc Châu năm 2015 là: Cuộc thi sáng tác biểu trưng (logo) Mộc Châu, quà tặng du lịch Mộc Châu, ảnh đẹp Mộc Châu xưa và nay; Du khách được trực tiếp tham gia vào các hoạt động văn hóa, văn nghệ, thể thao như tham gia Hoạt động cộng đồng đường phố, thi bắn nỏ, tùng còn, tham gia Vòng xòe đại với chủ đề “Ấn tượng Mộc Châu” diễn ra ngay sau Chương trình nghệ thuật đêm 1/9.</p>\r\n<p style=\"text-align: justify;\">Xem nội dung Kế hoạch tổ chức Ngày hội văn hóa các dân tộc năm 2015 <a href=\"images/Anh_tin_bai_2015/Van_ban/KH%20Ngay%20hoi%20VH%202015%20V1.doc\"><span style=\"color: #0000ff; font-size: small;\"><strong>Tại đây</strong></span></a></p>\r\n<p style=\"text-align: justify;\">Hiện tại, các cơ quan, đơn vị, UBND các xã, thị trấn đang tiếp tục triển khai thực hiện các công việc chuẩn bị cho Ngày hội đảm bảo đúng tiến độ, thời gian quy định, trong đó chú trọng công tác tuyên truyền, quảng bá, chỉnh trang đô thị, tu bổ, nâng cấp, dọn dẹp vệ sinh tại các điểm di tích danh thắng, quản lý giá cả dịch vụ…</p>','1','0','0','146','2015-07-30 08:40:49','196','','2015-07-30 10:24:11','196','196','2015-07-30 10:23:59','2015-07-30 08:40:49','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2014\\/Thang-9\\/5 9 2014 dh2.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','9','0','21','','','1','279','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
