insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('1999','PS Mộc Châu hoàn thành công tác chuẩn bị Đại hội đảng bộ lần thứ XXI nhiệm kỳ 2015 - 2020','ps-moc-chau-hoan-thanh-cong-tac-chuan-bi-dai-hoi-dang-bo-lan-thu-xxi-nhiem-ky-2015-2020','','<p style=\"text-align: justify;\"><span style=\"text-align: justify; font-size: 12.1599998474121px; line-height: 1.3em;\">Đại hội đảng bộ lần thứ XXI nhiệm kỳ 2015 – 2020 sẽ được tổ chức từ ngày 24 – 26/7/2015. Đến thời điểm này, mọi công tác chuẩn bị của huyện Mộc Châu đã hoàn tất. Trên khắp các ngả đường, ngõ xóm, đâu đâu cũng thấy không khí rộn ràng, rực rỡ cờ  hoa, băng rôn, chào mừng Đại hội Đảng các cấp, Đại hội Đại biểu Đảng bộ huyện lần thứ XXI.</span></p>\r\n','\r\n<p>{youtube}DTQfmHXkW0I{/youtube}</p>\r\n<p style=\"text-align: center;\"><em>Mộc Châu hoàn thành công tác chuẩn bị Đại hội đảng bộ lần thứ XXI</em></p>','1','0','0','142','2015-07-22 09:15:15','196','','2015-07-23 07:53:35','196','196','2015-07-23 07:53:21','2015-07-22 09:15:15','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_7\\/21 7 2015 ps4.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','9','0','3','','','1','182','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2001','Công an huyện Mộc Châu thăm hỏi gia đình liệt sỹ Bùi Công Nguyên nhân kỷ niệm 68 năm ngày thương binh liệt sỹ 27/7/1947 - 27/7/2015','cong-an-huyen-moc-chau-tham-hoi-gia-dinh-liet-sy-bui-cong-nguyen-nhan-ky-niem-68-nam-ngay-thuong-binh-liet-sy-27-7-1947-27-7-2015','','<p style=\"text-align: justify;\"><span style=\"font-family: times new roman,times; font-size: small;\">Hướng đến kỷ niệm 70 năm ngày thành lập lực lượng CAND và 68 năm ngày Thương binh liệt sĩ 27/7/1947-27/7/2015, ngày 23/7 cán bộ, chiến sỹ Công an huyện Mộc Châu đã đến thăm hỏi, động viên gia đình liệt sỹ Bùi Công Nguyên hy sinh 28/1/2015 trong quá trình thực hiện đợt cao điểm tấn công trấn áp các loại tội phạm bảo vệ Tết Nguyên đán Ất Mùi 2015.</span></p>\r\n','\r\n<h2 style=\"text-align: justify;\"><span style=\"font-family: times new roman,times; font-size: small;\">Hướng đến kỷ niệm 70 năm ngày thành lập lực lượng CAND và 68 năm ngày Thương binh liệt sĩ 27/7/1947-27/7/2015, ngày 23/7 cán bộ, chiến sỹ Công an huyện Mộc Châu đã đến thăm hỏi, động viên gia đình liệt sỹ Bùi Công Nguyên hy sinh 28/1/2015 trong quá trình thực hiện đợt cao điểm tấn công trấn áp các loại tội phạm bảo vệ Tết Nguyên đán Ất Mùi 2015.</span></h2>\r\n<p><span style=\"font-family: times new roman,times; font-size: small;\"><img src=\"images/Anh_tin_bai_2015/Thang_7/23 7 2015 tham.jpg\" border=\"0\" alt=\"\" /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: times new roman,times; font-size: small;\">Tại đây đoàn công tác đã trao tặng 10 triệu đồng cho gia đình liệt sỹ Bùi Công Nguyên, đây là số tiền do cán bộ, chiến sỹ công an huyện Mộc Châu quyên góp dịp 27/7. Trước anh linh liệt sỹ Bùi Công Nguyên, cán bộ chiến sỹ Công an huyện Mộc Châu cũng khẳng định sẽ tiếp tục nêu gương chiến đấu hy sinh của liệt sỹ Bùi Công Nguyên, đấu tranh không khoan nhượng với các loại tội phạm, giữ vững bình yên cuộc sống cho nhân dân.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: times new roman,times; font-size: small;\"><strong>Đức Cường</strong><br /></span></p>','1','0','0','25','2015-07-23 10:37:19','196','','2015-07-24 01:38:52','196','196','2015-07-24 01:38:38','2015-07-23 10:37:19','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_7\\/23 7 2015 tham.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','3','0','20','','','1','170','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2002','Công an huyện Mộc Châu lập chiến công chào mừng Đại hội đảng các cấp','cong-an-huyen-moc-chau-lap-chien-cong-chao-mung-dai-hoi-dang-cac-cap','','<p style=\"text-align: justify;\">Thiết thực thi đua lập thành tích chào mừng 70 năm thành lập lực lượng Công an nhân dân và chào mừng Đại hội Đảng bộ các cấp tiến tới Đại hội lần thứ XII của Đảng, trong thời gian qua Công an huyện Mộc Châu đã tích cực thi đua lập nhiều chiến công trên các mặt công tác.</p>\r\n','\r\n<p style=\"text-align: justify;\">Thiết thực thi đua lập thành tích chào mừng 70 năm thành lập lực lượng Công an nhân dân và chào mừng Đại hội Đảng bộ các cấp tiến tới Đại hội lần thứ XII của Đảng, trong thời gian qua Công an huyện Mộc Châu đã tích cực thi đua lập nhiều chiến công trên các mặt công tác.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_7/23 7 2015 tr.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Điển hình ngày 23/7 Cơ quan CSĐT Công an huyện Mộc Châu đã ra Quyết định khởi tố vụ án hình sự đối với bị can Lường Văn Giáp về tội: Lừa đảo chiếm đoạt tài sản của người dân tái định cư thủy điện Sơn La tại xã Tân Lập huyện Mộc Châu. Trước đó vào ngày 1/7,  lợi dụng sự thiếu hiểu biết của nhân dân tái định cư thủy điện Sơn La, đối tượng Lường Văn Giáp sinh năm 1962  trú quán tại  bản Nong La, phường Chiềng Sinh, thành phố Sơn La đã mạo danh là luật sư đang công tác tại văn phòng luật sư Trần Huấn, thuộc đoàn luật sư tỉnh Sơn La, và hứa giúp các gia đình ở xã Tân Lập đòi số tiền chênh lệch chi trả bồi thường di dân tái định cư. Theo đó đối tượng này đã yêu cầu những hộ gia đình có nhu cầu đòi thêm tiền đền bù - mỗi hộ nộp 5 triệu đồng lệ phí và tiền công để thực hiện việc đòi tiền đền bù. Hiện có 14  gia đình tại 2 bản Nập Tôm và Nà Tân xã Tân Lập đã nộp tổng số tiền 58 triệu đồng cho đối tượng này. Qua khám xét tại nhà đối tượng, cơ quan chức năng đã thu giữ nhiều tài liệu và giấy tờ có liên quan, đặc biệt đối tượng đang có ý định thu thập tài liệu để thực hiện hành vi lừa đảo tương tự tại các huyện khác ở tỉnh Sơn La.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_7/23 7 2015 tr1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Công an huyện Mộc Châu còn phá thành công vụ án trộm cắp xe máy, bắt giữ 2 đối là: Đặng Đình Huỳnh sinh năm 1989, trú tại Thôn Yên, xã Thạch Xá, huyện Thạch Thất, TP Hà Nội và Đặng Văn Hùng, sinh năm1989, trú tại bản Tây Hưng, xã Chiềng Hắc, huyện Mộc Châu, tỉnh Sơn La. Quá trình điều tra, công an huện Mộc Châu đã xác định đối tượng Đặng Đình Huỳnh là người thực hiện 2 vụ trộm cắp 2 xe máy YAMAHA - Exciter, BKS: 26H5 – 6723 của anh Mùa A Phư, trú tại xã Lóng Luông, huyện Vân Hồ và xe máy HONDA – Wave RSX, BKS: 26G1 – 003.70 của anh Lường Văn Chung, trú tại xã Mường Sang, huyện Mộc Châu. Còn đối tượng Đặng Văn Hùng là người mang xe máy do Đặng Đình Huỳnh trộm cắp về TP Hà Nội để tiêu thụ.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_7/23 7 2015 tr2.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Cũng trong ngày 23/7, Công an huyện Mộc Châu đã bắt giữ đối tượng Hạng A Cháng<strong>, </strong>sinh năm 1990, trú tại bản Ún, xã Mường Lý, huyện Mường Lát, tỉnh Thanh Hóa về hành vi trộm cắp tài sản. Trước đó vào ngày 17/7 Hạng A Cháng nhận dắt trâu thuê cho ông Giàng A Nu từ xã Mường Lý, huyện Mường Lát, tỉnh Thanh Hóa đến bản Pha Luông, xã Chiềng Sơn, huyện Mộc Châu, <strong> </strong>khi về đến nhà ông Giàng A Nu, đối tượng Hạng A Chángđã lợi dụng sơ hở của chủ nhà để lấy trộm số tiền 9.400.000đ. Tại cơ quan chức năng, Cháng khai nhận bản thân nghiện ma túy nên đã trộm cắp tiền để mua ma túy về sử dụng.</p>\r\n<p style=\"text-align: justify;\">Hiện Công an huyện Mộc Châu đang tiếp tục hoàn chỉnh hồ sơ để xử lý các đối tượng theo quy định của pháp luật.</p>\r\n<p style=\"text-align: right;\"><strong>Đức Cường</strong></p>','1','0','0','18','2015-07-23 10:44:19','196','','2015-07-28 03:09:04','196','196','2015-07-28 03:08:54','2015-07-23 10:44:19','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_7\\/23 7 2015 tr.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','3','0','13','','','1','266','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
