insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2030','Xét xử lưu động 2 vụ án ma túy tại xã Mường Sang','xet-xu-luu-dong-2-vu-an-ma-tuy-tai-xa-muong-sang','','<p style=\"text-align: justify;\">Nhằm tăng cường công tác tuyên truyền pháp luật và phòng chống tội phạm về ma túy tại các điểm phức tạp về tội phạm ma túy trên địa bàn huyện Mộc Châu, ngày 12/8, tại xã Mường Sang, Toà án nhân dân huyện Mộc Châu đã mở phiên tòa xét xử lưu động 2 vụ án đối với 4 bị cáo liên quan đến ma túy.</p>\r\n','\r\n<p style=\"text-align: justify;\">Nhằm tăng cường công tác tuyên truyền pháp luật và phòng chống tội phạm về ma túy tại các điểm phức tạp về tội phạm ma túy trên địa bàn huyện Mộc Châu, ngày 12/8, tại xã Mường Sang, Toà án nhân dân huyện Mộc Châu đã mở phiên tòa xét xử lưu động 2 vụ án đối với 4 bị cáo liên quan đến ma túy.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/13 8 2015 toa.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Vụ án thứ nhất xét xử đối với 2 bị cáo là Tô Văn Trịnh, sinh năm 1969 và bị cáo Nguyễn Văn Hán, sinh năm 1988 đều trú quán tại xã Chiềng Sơn, huyện Mộc Châu. Theo cáo trạng của Việt Kiểm sát nhân dân huyện Mộc Châu, ngày 4/5/2015 Tô Văn Trịnh gọi điện thoại thuê Nguyễn Văn Hán chở đi tìm mua ma túy và hứa trả công cho Hán 150.000 đồng và cho cùng sử dụng ma túy sau khi mua được.  2 đối tượng đã đi xe máy lên khu vực biên giới xã Lóng Sập mua được 230 viên hồng phiến và 6,2g hêrôin. Trên đường về thì bị Tổ công tác Đồn Biên phòng cửa khẩu Lóng Sập bắt giữ.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/13 8 2015 toa1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Vụ án thứ 2 xét xử 2 bị cáo là Lừ Văn Chiến, sinh năm 1974 trú quán tại xã Tú Nang, huyện Yên Châu và bị cáo Sa Đức Văn, sinh năm 1993, trú quán tại Bản Lùn xã Mường Sang, huyện Mộc Châu. Theo cáo trạng, vào ngày 11/6 hai đối tượng đã rủ nhau cùng vào bản Co Sung, xã Đông Sang, huyện Mộc Châu mua ma túy về sử dụng và bị lực lực lượng chức năng bắt quả tang, vật chứng thu giữ là 1 gói hêrôin có trọng lượng 0,44g.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/13 8 2015 toa2.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Tại phiên tòa, các bị cáo đều khai mình là người nghiện ma túy và khai nhận toàn bộ hành vi phạm tội. Căn cứ vào các chứng cứ được thẩm tra tại phiên tòa và kết quả tranh tụng, trên cơ sở xem xét đầy đủ, toàn diện chứng cứ, áp dụng các điều khoản của Bộ luật hình sự, Hội đồng xét xử đã tuyên phạt bị cáo Tô Văn Trịnh 8 năm 6 tháng tù giam, Nguyễn Văn Hán  8 năm tù giam, Lừ Văn Chiến 30 tháng tù giam và bị cáo Sa Đức Văn 26 tháng tù giam.</p>\r\n<p> </p>\r\n<p style=\"text-align: right;\"><strong>Đức Cường</strong></p>','1','0','0','18','2015-08-13 10:16:44','196','','2015-08-13 10:16:58','196','196','2015-08-13 10:16:49','2015-08-13 10:16:44','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_8\\/13 8 2015 toa.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','9','','','1','95','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2031','Đoàn thanh niên thị trấn Mộc Châu tổ chức các hoạt động hè cho thanh thiếu niên','doan-thanh-nien-thi-tran-moc-chau-to-chuc-cac-hoat-dong-he-cho-thanh-thieu-nien','','<p style=\"text-align: justify;\">Thực hiện chương trình công tác đoàn và phong trào thanh thiếu nhi năm 2015, trong 2 tháng qua, Ban chấp hành Đoàn thị trấn Mộc Châu đã chỉ đạo 15 chi đoàn tiểu khu triển khai nhiều hoạt động hè phong phú, đa dạng, đáp ứng nhu cầu vui chơi, giải trí trong dịp hè cho thanh thiếu nhi.</p>\r\n','\r\n<p style=\"text-align: justify;\">Thực hiện chương trình công tác đoàn và phong trào thanh thiếu nhi năm 2015, trong 2 tháng qua, Ban chấp hành Đoàn thị trấn Mộc Châu đã chỉ đạo 15 chi đoàn tiểu khu triển khai nhiều hoạt động hè phong phú, đa dạng, đáp ứng nhu cầu vui chơi, giải trí trong dịp hè cho thanh thiếu nhi.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/13 8 2015 dtn.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Nhiều hoạt động được tổ chức đã thu hút, tập hợp đông đảo thanh niên tham gia như tổ chức luyện tập văn nghệ, tổ chức các trò chơi dân gian, tổ chức các phong trào giáo dục truyền thống, phong trào đền ơn đáp nghĩa uống nước nhớ nguồn…Đây là các hoạt động bổ ích, thiết thực, phù hợp với tâm lý lứa tuổi thanh thiếu nhi, giúp các em rèn luyện phẩm chất, trí tuệ, kỹ năng giao tiếp, đồng thời giáo dục truyền thống cách mạng, lý tưởng sống, tuyền truyền nhắc nhở các em tránh xa các tệ nạn xã hội.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/13 8 2015 dtn3.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Chương trình hoạt động hè được tổ chức hàng năm không những giúp các em thiếu niên – nhi đồng được rèn luyện về kỹ năng sống, tính kỷ luật cao, có tinh thần vượt qua khó khăn; đoàn kết giúp đỡ lẫn nhau; mà còn hun đúc hoài bão, lý tưởng, trách nhiệm cho các em; động viên các em vươn lên trong học tập và rèn luyện, xứng đáng là con ngoan trò giỏi, cháu ngoan Bác Hồ - Chủ nhân tương lai của đất nước.</p>\r\n<p style=\"text-align: right;\"><strong> Đức Cường</strong></p>','1','0','0','25','2015-08-13 10:21:03','196','','2015-08-13 10:21:24','196','196','2015-08-13 10:21:09','2015-08-13 10:21:03','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_8\\/13 8 2015 dtn3.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','10','','','1','82','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2032','Đại hội đại biểu hội người cao tuổi thị trấn nông trường Mộc Châu lần thứ VI','dai-hoi-dai-bieu-hoi-nguoi-cao-tuoi-thi-tran-nong-truong-moc-chau-lan-thu-vi','','<p style=\"text-align: justify;\">Ngày 12/8 Hội người Cao tuổi thị trấn Nông trường Mộc Châu đã long trọng tổ chức Đại hội Đại biểu Hội Người cao tuổi lần thứ VI nhiệm kỳ 2015-2020. Đây là Đại hội được Ban đại diện Hội người Cao tuổi huyện Mộc Châu chọn làm Đại hội điểm.</p>\r\n','\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12.1599998474121px; line-height: 1.3em;\">Ngày 12/8 Hội người Cao tuổi thị trấn Nông trường Mộc Châu đã long trọng tổ chức Đại hội Đại biểu Hội Người cao tuổi lần thứ VI nhiệm kỳ 2015-2020. Đây là Đại hội được Ban đại diện Hội người Cao tuổi huyện Mộc Châu chọn làm Đại hội điểm.</span></p>\r\n<p style=\"text-align: justify;\"><span><img src=\"images/Anh_tin_bai_2015/Thang_8/13 8 2015 nct.jpg\" border=\"0\" alt=\"\" /></span></p>\r\n<p style=\"text-align: justify;\">Nhiệm kỳ qua, dưới sự lãnh đạo của Ban đại diện Hội Người cao tuổi huyện, của Đảng ủy, HĐND, UBND thị trấn và sự nỗ lực, trách nhiệm của hội viên NCT, công tác Hội Người cao tuổi thị trấn Nông trường đã có nhiều chuyển biến tích cực. 100% hội viên NCT triển khai tốt phong trào “Tuổi cao chí càng cao, nêu gương sáng vì sự nghiệp xây dựng và bảo vệ Tổ quốc” và các phong trào thi đua của Hội cũng như của địa phương.</p>\r\n<p style=\"text-align: justify;\">5 năm qua, Hội Người cao tuổi thị trấn duy trì hoạt động của 39 chi hội, phát triển mới được 865 hội viên, tỷ lệ tập hợp vào tổ chức Hội đạt 95%. Kết quả phân xếp loại hàng năm có 93% chi hội hoàn thành xuất sắc và hoàn thành tốt nhiệm vụ. Công tác chăm sóc, phụng dưỡng người cao tuổi thực hiện hiệu quả, đã tổ chức khám sức khỏe định kỳ cho người cao tuổi; thăm hỏi, động viên NCT khi ốm đau. Chất lượng sinh hoạt Hội từng bước được nâng lên. Người cao tuổi tin tưởng vào chủ trương, đường lối của Đảng, chính sách, pháp luật của Nhà nước, phát huy tinh thần \"tuổi cao, chí càng cao\" trong đời sống xã hội, tích cực tham gia các phong trào thi đua, góp phần thúc đẩy KT-XH thị trấn ngày càng phát triển.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/13 8 2015 nct2.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Tại Đại hội, các đại biểu đã thảo luận và đề ra nhiệm vụ trọng tâm trong nhiệm kỳ tới với mục tiêu: phấn đấu mỗi năm kết nạp được100 hội viên trở lên, 100% hội viên được chăm sóc sức khỏe ban đầu. Phấn đấu có 90% chi hội hoàn thành tốt và hoàn thành xuất sắc nhiệm vụ, 85% hội viên đạt danh hiệu tuổi cao gương sáng.</p>\r\n<p style=\"text-align: justify;\">Cũng tại Đại hội các đại biểu đã bầu Ban chấp hành gồm 17 đồng chí, Bà Đỗ Thị Nụ được bầu giữ chức Chủ tịch Hội người cao tuổi thị trấn Nông trường khóa VI, nhiệm kỳ 2015-2020.</p>\r\n<p style=\"text-align: justify;\">Nhân dịp này UBND Thị trấn Nông trường Mộc Châu đã khen thưởng cho 4 tập thể và 8 cá nhân có thành tích xuất sắc trong công tác hội NCT thị trấn nhiệm kỳ 2010-2015.</p>\r\n<p style=\"text-align: right;\"><strong> Đức Cường</strong></p>','1','0','0','9','2015-08-13 10:16:58','196','','2015-08-13 10:23:47','196','196','2015-08-13 10:23:40','2015-08-13 10:16:58','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_8\\/13 8 2015 nct2.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','5','0','8','','','1','104','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
