insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2067','Thử tài cùng các môn thể thao dân tộc','thu-tai-cung-cac-mon-the-thao-dan-toc','','<p style=\"text-align: justify;\">Bên cạnh việc khám phá tìm hiểu những nét văn hoá vô cùng và đặc sắc của đồng bào các dân tộc Mộc Châu, đến với Ngày hội Văn hoá các dân tộc Mộc Châu 2015 du khách được khám phá, “mục sở thị” các vận động viên không chuyên đến từ các xã, thị trấn thi đấu các môn thi đấu thể thao dân tộc. Đặc biệt, du khách có thể thử sức mình trong một số môn thể thao dân tộc.</p>\r\n','\r\n<p style=\"text-align: justify;\">Bên cạnh việc khám phá tìm hiểu những nét văn hoá vô cùng và đặc sắc của đồng bào các dân tộc Mộc Châu, đến với Ngày hội Văn hoá các dân tộc Mộc Châu 2015 du khách được khám phá, “mục sở thị” các vận động viên không chuyên đến từ các xã, thị trấn thi đấu các môn thi đấu thể thao dân tộc. Đặc biệt, du khách có thể thử sức mình trong một số môn thể thao dân tộc.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/28 8 2015 tt.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Năm nay nội dung thi đấu các môn thể thao dân tộc, trò chơi dân gian thể thao, các vận động viên sẽ tranh tài ở các nội dung như kéo co, đẩy gậy, bắn nỏ, đánh tu lu… thể hiện đời sống lao động, sự dũng cảm, mưu trí trong chinh phục thiên nhiên, thử thách của đồng bào các dân tộc huyện Mộc Châu. Những trận thi đấu sôi nổi, hào hứng thể hiện sức mạnh, sự khéo léo,  tinh thần thượng võ của các vận động viên.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/28 8 2015 tt1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Thi đấu các môn thể thao dân tộc là hoạt động đầu tiên diễn ra trong Ngày hội Văn hóa các dân tộc huyện Mộc Châu năm 2015. Địa điểm tại sân vận động huyện Mộc Châu.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/28 8 2015 tt2.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: right;\"><strong>Thùy Mai</strong></p>','1','0','0','146','2015-08-28 03:41:51','196','','2015-08-28 03:42:18','196','196','2015-08-28 03:41:55','2015-08-28 03:41:51','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_8\\/28 8 2015 tt.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','14','','','1','59','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2068','Khám phá Lễ hội đường phố Mộc Châu sôi động và đặc sắc','kham-pha-le-hoi-duong-pho-moc-chau-soi-dong-va-dac-sac','','<p style=\"text-align: justify;\">Một hoạt động trong tổng thể chương trình Ngày hội văn hóa các dân tộc huyện Mộc Châu năm 2015 đem đến không khí vô cùng sôi động bởi âm thanh và màu sắc đặc sắc trong Ngày hội Văn hóa các dân tộc huyện Mộc Châu 2015 là Lễ hội đường phố.</p>\r\n','\r\n<p style=\"text-align: justify;\">Một hoạt động trong tổng thể chương trình Ngày hội văn hóa các dân tộc huyện Mộc Châu năm 2015 đem đến không khí vô cùng sôi động bởi âm thanh và màu sắc đặc sắc trong Ngày hội Văn hóa các dân tộc huyện Mộc Châu 2015 là Lễ hội đường phố.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/28 8 2015 dp2.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Trong Lễ hội đường phố đồng bào dân tộc Mông sẽ thể hiện những nét văn hóa độc đáo, đặc sắc  trong điệu nhảy khỏe khoắn như núi, dẻo dai như cây Lanh vàtiếng Khèn gọi bạn ngọt ngào đắm. Điệu nhảy khèn của những chàng trai Mông có động tác như: đưa chân, quay đổi chỗ, vờn khèn, quay gót... Âm điệu, tiết tấu của tiếng khèn cũng hết sức trau chuốt lúc trầm buồn, da diết, dìu dặt, tha thiết, lúc lại vui tươi, rộn ràng. Điệu múa Khèn thể hiện sức mạnh, sự dẻo dai của các chàng trai dân tộc Mông và tiếng Khèn Mông vừa mang âm hưởng bao la hùng vĩ của núi rừng, vừa mang nét chân thành, giản dị.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/28 8 2015 dp4.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Đồng bào dân tộc Mông không chỉ mang đến Lễ hội đường phố điệu múa khèn đặc sắc mà còn tái hiện hình ảnh người vợ dắt ngựa, chở chồng sau mỗi đêm hội trở về. Đây chắc chắn là hình ảnh khó phai đối với bất kỳ ai khi đến với  Mộc Châu trong dịp Ngày hội Văn hoá các dân tộc.</p>\r\n<p style=\"text-align: justify;\">Trái ngược với điệu múa Khèn khoẻ khoắn của các chàng trai Mông những cô gái người Mường mang đến với Lễ hội với sự dịu dàng, đằm thắm lại mang thể hiện qua giai điệu của những ca khúc hoa đất mường, hát mời trầu, múa bông… cùng với âm hưởng của những chiếc Cồng, Chiêng là loại đạo cụ mang ý nghĩa sâu sắc trong đời sống văn hóa tinh thần cũng như tâm linh của đồng bào dân tộc Mường<em>. </em> </p>\r\n<p style=\"text-align: justify;\">Cùng với đồng bào các dân tộc, trong lễ hội đường phố ở Mộc Châu các chàng trai, cô gái Dao say mê trong điệu múa chuông rộn rã và sinh động. Với đạo cụ chính là chiếc chuông nhỏ bằng đồng kết hợp với trống, chiêng để tạo nên âm thanh sôi động.  Điệu múa chuông của người Dao là sự kết hợp nhịp nhàng của sự khỏe khoắn, dẻo dai của các chàng trai và các động tác nhún chân mềm mại, duyên dáng của các cô gái.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/28 8 2015 dp3.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Đối với đồng bào dân tộc Thái tham dự vào Lễ hội đường phố sẽ tái hiện lại Lễ hội Cầu mưahay còn gọi là Lễ hội Xến Xó Phốn. Đây là Lễ hội cầu cho mưa thuận gió hòa, mùa màng tươi tốt, con người được sống trong ấm no hạnh phúc. Lễ hội ra đời và hình thành cùng kho tàng văn hóa phi vật thể của người Thái. Do người Thái canh tác nông nghiệp chủ yếu dựa vào thiên nhiên nên họ cũng rất tin tưởng ở các vị thần. Họ tin rằng khi thật tâm cầu xin, các vị thần sẽ mang lại cho dân làng sự ấm no, hạnh phúc.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/28 8 2015 dp.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Lễ hội đường phố trong Ngày hội Văn hoá các dân tộc  Mộc Châu với sự tham gia của các dân tộc Mông, Dao, Thái, Mường, Kinh đã thể hiện rõ nét những nét đẹp văn hóa đặc sắc và vô cùng độc đáo của các dân tộc. Mỗi dân tộc có những nét văn hóa đặc trưng riêng đã tạo nên một “bức tranh” phong phú, đa sắc màu, đậm đà bản sắc của đồng bào các dân tộc đang sinh sống trên mảnh đất cao nguyên tươi đẹp đồng thời thể hiện tinh thần đoàn kết, gắn bó giữa các dân tộc trong cuộc sống, lao động sản xuất để xây dựng Mộc Châu ngày phát triển.</p>\r\n<p style=\"text-align: right;\"><strong>Lường Mai</strong></p>','1','0','0','146','2015-08-28 03:53:43','196','','2015-08-28 03:53:59','196','196','2015-08-28 03:53:49','2015-08-28 03:53:43','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_8\\/28 8 2015 dp.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','13','','','1','121','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2069','Công an huyện Mộc Châu phá thành công chuyên án trộm cắp tài sản tại Bệnh viện đa khoa Mộc Châu','cong-an-huyen-moc-chau-pha-thanh-cong-chuyen-an-trom-cap-tai-san-tai-benh-vien-da-khoa-moc-chau','','<p style=\"text-align: justify;\">Thực hiện đợt cao điểm đấu tranh phòng chống tội phạm trện địa bàn tỉnh Sơn La và công tác đảm bảo an ninh trật tự cho Ngày Hội văn hóa các dân tộc huyện Mộc Châu năm 2015, ngày 28/8 Công an huyện Mộc Châu đã phá thành công chuyên án 315B bắt giữ đối tượng Đỗ Văn Bình về hành vi trộm cắp tài sản tại Bệnh viện Đa khoa huyện Mộc Châu.</p>\r\n','\r\n<p style=\"text-align: justify;\">Thực hiện đợt cao điểm đấu tranh phòng chống tội phạm trện địa bàn tỉnh Sơn La và công tác đảm bảo an ninh trật tự cho Ngày Hội văn hóa các dân tộc huyện Mộc Châu năm 2015, ngày 28/8 Công an huyện Mộc Châu đã phá thành công chuyên án 315B bắt giữ đối tượng Đỗ Văn Bình về hành vi trộm cắp tài sản tại Bệnh viện Đa khoa huyện Mộc Châu.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/28 8 2015 tr.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Trong thời gian qua, lợi dụng Bệnh viện Đa khoa huyện Mộc Châu đang trong giai đoạn xây dựng, không có hệ thống hàng rào bảo vệ, cộng với tình trạng bệnh nhân đông, các đối tượng trộm cắp đã lẻn vào lấy cắp tài sản của bệnh viện và người nhà bệnh nhân.</p>\r\n<p style=\"text-align: justify;\">Điển hình ngày 25/8/2015 đối tượng Đỗ Văn Bình, sinh năm 1983, trú tại tiểu khu 13, thị trấn Mộc Châu đã lợi dụng sơ hở trong việc trông coi quản lý tài sản của chị Vì Thị Xương tại khu điều trị nội trú của bệnh viện để lấy trộm 01 túi xách trong đó có 8,3 triệu đồng và 1 điện thoại di động.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/28 8 2015 tr1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Sau khi nhận được tin báo của bệnh viện, Đội cảnh sát điều tra về TTXH Công an huyện Mộc Châu đã sử dụng biện pháp nghiệp vụ, sàng lọc các đối tượng khả nghi, đồng thời phối hợp theo dõi qua hệ thống camera giám sát của bệnh viện, qua đó đã xác định và bắt giữ được đối tượng.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/28 8 2015 tr2.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Tình trạng trộm cắp tại bệnh viện và những nơi công cộng đã được báo chí phản ánh rất nhiều, nguyên nhân chính là kẻ gian đã lợi dụng sự sơ hở của nhân dân để trộm cắp tài sản, do đó cùng với việc tăng cường kiểm tra giám sát của các cơ quan chức năng, trước hết mỗi người dân cần nêu cao tinh thần cảnh giác trước tội phạm để bảo vệ tài sản của chính mình.</p>\r\n<p style=\"text-align: right;\"><strong>Đức Cường</strong></p>','1','0','0','18','2015-08-28 07:53:54','196','','2015-08-28 07:54:10','196','196','2015-08-28 07:53:59','2015-08-28 07:53:54','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_8\\/28 8 2015 tr.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','4','','','1','118','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2070','Đồn biên phòng cửa khẩu quốc gia Lóng Sập huyện Mộc Châu tỉnh Sơn La bắt giữ đối tượng vận chuyển 3.800 viên ma túy tổng hợp','don-bien-phong-cua-khau-quoc-gia-long-sap-huyen-moc-chau-tinh-son-la-bat-giu-doi-tuong-van-chuyen-3800-vien-ma-tuy-tong-hop','','<p style=\"text-align: justify;\">Hồi 6h ngày 29/8, tại km105+500 quốc lộ 43 thuộc địa phận bản Phát, xã Lóng Sập, huyện Mộc Châu, tỉnh Sơn La, Đồn biên phòng Quốc gia Cửa khẩu Lóng Sập chủ trì phối hợp với Phòng Phòng chống ma túy Bộ đội Biên phòng tỉnh Sơn La</p>\r\n','\r\n<p style=\"text-align: justify;\">Hồi 6h ngày 29/8, tại km105+500 quốc lộ 43 thuộc địa phận bản Phát, xã Lóng Sập, huyện Mộc Châu, tỉnh Sơn La, Đồn biên phòng Quốc gia Cửa khẩu Lóng Sập chủ trì phối hợp với Phòng Phòng chống ma túy Bộ đội Biên phòng tỉnh Sơn La, Đội BP 14 bộ đội biên phòng, Công an huyện Mộc Châu phá thành công chuyên án mang bí số 171L bắt giữ đối tượng Hầu A Dế, sinh năm 1985, trú quán tại Tiểu khu Pa Khen I, thị trấn Nông trường Mộc Châu, huyện Mộc Châu, tỉnh Sơn La đang vận chuyển trái phép 3800 viên ma túy tổng hợp.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/29 8 2015 mt.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Theo lời khai ban đầu: Vào sáng ngày 29/8 Dế có nhận lời vận chuyển thuê số ma túy trên cho một đối tượng khác không biết tên từ khu vực biên giới ra thị trấn Mộc Châu với giá 2 triệu đồng.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/20 8 2015 mt.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Nhằm qua mắt lực lượng chức năng, đối tượng đã dùng thủ đoạn là gói số ma túy trên vào túi nilon rồi cho vào bầu lọc gió của xe máy. Khi bị phát hiện đối tượng chống trả quyết liệt nhưng đã bị lực lượng chức năng khống chế, bắt giữ.</p>\r\n<p style=\"text-align: justify;\">Hiện lực lượng chức năng đang hoàn chỉnh hồ sơ để xử lý đối tượng theo quy định của Pháp luật.</p>\r\n<p style=\"text-align: right;\"><strong>Đức Cường</strong></p>','1','0','0','18','2015-08-29 11:02:02','196','','2015-08-29 11:02:17','196','0','0000-00-00 00:00:00','2015-08-29 11:02:02','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_8\\/29 8 2015 mt.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','3','','','1','112','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2071','Thứ trưởng Bùi Văn Nam trao tặng trường mẫu giáo xã Chiềng Hắc, huyện Mộc Châu, tỉnh Sơn La','thu-truong-bui-van-nam-trao-tang-truong-mau-giao-xa-chieng-hac-huyen-moc-chau-tinh-son-la','','<p style=\"text-align: justify;\">Ngày 28/8, đồng chí Thượng tướng Bùi Văn Nam, Ủy viên Trung ương Đảng, Thứ trưởng Bộ Công an và đoàn công tác, đã cùng lãnh đạo huyện Mộc Châu, Công an tỉnh Sơn La, Công an huyện Mộc Châu, tổ chức lễ khánh thành, trao tặng công trình trường học mẫu giáo có quy mô 2 phòng, được xây dựng khép kín cho cô, bé trường mầm non xã Chiềng Hắc, huyện Mộc Châu, tỉnh Sơn La.</p>\r\n','\r\n<p style=\"text-align: justify;\">Ngày 28/8, đồng chí Thượng tướng Bùi Văn Nam, Ủy viên Trung ương Đảng, Thứ trưởng Bộ Công an và đoàn công tác, đã cùng lãnh đạo huyện Mộc Châu, Công an tỉnh Sơn La, Công an huyện Mộc Châu, tổ chức lễ khánh thành, trao tặng công trình trường học mẫu giáo có quy mô 2 phòng, được xây dựng khép kín cho cô, bé trường mầm non xã Chiềng Hắc, huyện Mộc Châu, tỉnh Sơn La.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/29 8 2015 tt.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Công trình là một ngôi nhà cấp 4 kiên cố (tường xây, mái lợp bằng tôn lạnh) được chia làm các phòng học, nơi ăn, nghỉ, vui chơi cho các bé, diện tích xây dựng khoảng 200 m<sup>2</sup>. Những năm qua, Đảng, Nhà nước, chính quyền và các đoàn thể địa phương đã rất quan tâm hỗ trợ xã Chiềng Hắc trong việc xóa đói, giảm nghèo. Nhằm đảm bảo cơ sở vật chất cho địa phương chăm lo, đầu tư vào phát triển giáo dục, đồng chí Thứ trưởng Bùi Văn Nam và Công an tỉnh Sơn La đã kêu gọi sự hảo tâm của một số đơn vị cùng với số tiền của gia đình đồng chí ủng hộ, giúp đỡ đóng góp được hơn 01 tỉ đồng xây dựng công trình trường học cho cô trò Trường mần non Chiềng Hắc.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/29 8 2015 tt1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Phát biểu tại buổi lễ, Thượng tướng Bùi Văn Nam mong muốn, với công trình trường học này, Đảng bộ, chính quyền và nhân dân địa phương sẽ phát huy hết công năng sử dụng, giúp các em học sinh có điều kiện tốt hơn trong học tập, trở thành những người tài, có ích cho gia đình, cộng đồng và xã hội.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/29 8 2015 tt2.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Nhân dịp này, đồng chí Thứ trưởng Bùi Văn Nam và lãnh đạo Công an tỉnh Sơn La đã tổ chức các hoạt động trồng cây và trao tặng 01 ti vi, cùng hơn 100 cặp sách các loại cho cô và bé Trường mầm non Chiềng Hắc trước thềm học mới 2015 - 2016.</p>\r\n<p style=\"text-align: right;\"><strong>Trọng Đạt ANTV</strong></p>','1','0','0','9','2015-08-29 12:02:06','196','','2015-08-29 12:02:23','196','196','2015-08-29 12:02:10','2015-08-29 12:02:06','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_8\\/29 8 2015 tt.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','3','','','1','66','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2072','63 tác giả tham gia cuộc thi sáng tác biểu trưng Mộc Châu  ảnh đẹp Mộc Châu xưa và nay năm 2015','63-tac-gia-tham-gia-cuoc-thi-sang-tac-bieu-trung-moc-chau-anh-dep-moc-chau-xua-va-nay-nam-2015','','<p style=\"text-align: justify;\">Ngày 29/8, Ban tổ chức cuộc thi sáng tác biểu trưng (logo) Mộc Châu, ảnh đẹp Mộc Châu xưa và nay đã tổ chức họp, thành lập Ban giám khảo chấm các tác phẩm dự thi.</p>\r\n','\r\n<p style=\"text-align: justify;\">Ngày 29/8, Ban tổ chức cuộc thi sáng tác biểu trưng (logo) Mộc Châu, ảnh đẹp Mộc Châu xưa và nay đã tổ chức họp, thành lập Ban giám khảo chấm các tác phẩm dự thi.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/29 8 2015 lg.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Đây là lần đầu tiên huyện Mộc Châu tổ chức thi sáng tác biểu trưng (logo) Mộc Châu, ảnh đẹp Mộc Châu xưa và nay, tuy nhiên cuộc thi đã thu hút được gần 100 logo, trên 200 ảnh đẹp của đông đảo các tác giả, các nghệ sĩ nhiếp ảnh đến từ nhiều tỉnh thành phố trung tâm toàn quốc như thành phố Hà Nội, Hồ Chí Minh, Thanh Hóa, Đồng Tháp, Huế, Đà Nẵng, Sơn La. Đối với biểu trưng Mộc Châu, yêu cầu phản ánh được những nét tiêu biểu, đặc trưng và nổi bật nhất về văn hóa, con người, tài nguyên, thiên nhiên Mộc Châu. Tác phẩm dự thi không được trùng lặp, gây nhầm lẫn với biểu trưng, biểu tượng, hình ảnh, nhãn hiệu của các cơ quan, đơn vị, tổ chức, cá nhân trong và ngoài nước. Thi sưu tầm ảnh Mộc Châu xưa là những bức ảnh phản ánh về khung cảnh Mộc Châu xưa gồm mọi hoạt động kinh tế, văn hóa, chính trị từ trước những năm 1986. Đối với ảnh đẹp Mộc Châu mang chủ đề du lịch Mộc Châu phản ánh nét đặc sắc, phong phú, đa dạng về cuộc sống, nét sinh hoạt văn hóa độc đáo của các dân tộc Mộc Châu, tài nguyên du lịch, đặc biệt là những cảm nhận về những công trình du lịch - văn hóa, điểm du lịch, di sản văn hóa, lễ hội, làng nghề truyền thống…  Nhằm chọn ra biểu trưng xuất sắc, tiêu biểu nhất, đồng thời sưu tầm, lưu giữ những bức ảnh đẹp của Mộc Châu xưa và nay, phát huy sức sáng tạo của đông đảo tác giả, tạo ra những tác phẩm mới có giá trị trong việc giới thiệu, quảng bá hình ảnh mảnh đất, con người, văn hóa, du lịch Mộc Châu đến với du khách trong nước và quốc tế.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/29 8 2015 lg1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Ban tổ chức hội thi đã mời các nhà chuyên môn có kinh nghiệm và uy tín trong nước tham gia làm ban giám khảo cuộc thi. Ban giám khảo sẽ chọn ra 100 bức ảnh đẹp để trưng bày triển lãm tại Nhà Văn hóa huyện Mộc Châu và 5 biểu trưng xuất sắc nhất để lấy ý kiến công chúng và trao giải.</p>\r\n<p style=\"text-align: right;\"><strong>Thùy Mai</strong></p>','1','0','0','146','2015-08-29 12:21:17','196','','2015-08-29 12:21:33','196','196','2015-08-29 12:21:23','2015-08-29 12:21:17','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_8\\/29 8 2015 lg.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','12','','','1','132','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2073','Bản Áng 3 xã Đông Sang chuẩn bị Hội thi ẩm thực dân tộc ','ban-ang-3-xa-dong-sang-chuan-bi-hoi-thi-am-thuc-dan-toc','','<p style=\"text-align: justify;\">Thi ẩm thực dân tộc Mộc Châu là một trong những nội dung được tổ chức trong Ngày hội Văn hoá các dân tộc huyện Mộc Châu từ ngày 31/1 đến ngày 2/9. Tham dự nội dung thi này, năm nay có 7 đội thi đến từ các xã Đông Sang và Mường Sang và thị trấn Nông Trường  Mộc Châu  </p>\r\n','\r\n<p style=\"text-align: justify;\">Thi ẩm thực dân tộc Mộc Châu là một trong những nội dung được tổ chức trong Ngày hội Văn hoá các dân tộc huyện Mộc Châu từ ngày 31/1 đến ngày 2/9. Tham dự nội dung thi này, năm nay có 7 đội thi đến từ các xã Đông Sang và Mường Sang và thị trấn Nông Trường  Mộc Châu </p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/30 8 2015 lua1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Để thực hiện tốt nội dung thi “Ẩm thực các dân tộc Mộc Châu ”, thu hút sự tham gia của du khách cũng như giới thiệu các món ăn đặc trưng của dân tộc, những ngày này các thành viên trong Đội thi của Bản Áng 3, xã Đông Sang, huyện Mộc Châu đang ra sức tập luyện và chuẩn bị các điều kiện cho việc tái hiện lại quy trình nấu ăn còn rất thô sơ của người Thái xưa như: Lấy lửa bằng các hình thức cọ sát vật liệu tự nhiên; nấu cơm bằng gạo tự giã; chế biến các món ăn và trình bày mân cỗ.</p>\r\n<p style=\"text-align: justify;\">Trong Hội thi ẩm thực dân tộc Mộc Châu có lẽ hồi hộp và kịch tính nhất là màn lấy lửa bằng các hình thức cọ sát vật liệu tự nhiên mà không được dùng bất cứ phương tiện lấy lửa hiện đại nào. Để việc lấy lửa được nhanh nhất đòi hỏi sự chuẩn bị vô cùng kỹ lưỡng và cẩn thận từ việc chọn thanh tre để cọ sát, làm bùi nhùi để tiếp lửa.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/30 8 2015 lua.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Các món ăn của người Thái đều được chế biến từ nguồn nguyên liệu hoàn toàn tự nhiên. Điều làm nên vị đặc biệt của ẩm thực dân tộc Thái chính là các gia vị của núi rừng được phối trộn hài hòa giữa các vị đắng- cay- mặn- chát, mang đặc trưng riêng không thể lẫn với bất kỳ dân tộc nào khác. Nhờ bàn tay khéo léo của cô gái Thái sẽ tạo nên những món ăn truyền thống mà ai đã từng được thưởng thức một lần đều sẽ nhớ mãi... Bởi người Thái Mộc Châu đãi khách không chỉ bằng những sản vật của núi rừng mà còn bằng tấm lòng hiếu khách, bằng tình cảm của người chế biến gửi gắm trong mỗi món ăn.</p>\r\n<p style=\"text-align: justify;\">Các thành viên Đội thi bản Áng 3, xã Đông Sang đang mong chờ và tập luyện rất kỹ lưỡng nhằm lưu giữ, bảo tồn những nét văn hóa đặc sắc, riêng có của dân tộc Thái và giới thiệu những nét văn hóa ẩm thực đặc sắc đến đông đảo du khách trong Ngày hội Văn hóa các dân tộc Mộc Châu diễn ra từ ngày 31/8 đến 2/9/2015.</p>\r\n<p style=\"text-align: right;\"><strong>Thùy Mai</strong></p>','1','0','0','146','2015-08-30 09:58:49','196','','2015-08-30 09:59:37','196','196','2015-08-30 09:59:23','2015-08-30 09:58:49','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_8\\/30 8 2015 lua1.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','3','0','11','','','1','57','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2074','Bí thư Huyện ủy, Chủ tịch UBND kiểm tra công tác chuẩn bị cho ngày 2/9','bi-thu-huyen-uy-chu-tich-ubnd-kiem-tra-cong-tac-chuan-bi-cho-ngay-2-9','','<p style=\"text-align: justify;\">Để đảm bảo cho Ngày hội văn hóa huyện Mộc Châu năm 2015 diễn ra thành công, tạo ấn tượng tốt đẹp với du khách, ngày 30/8 Đ/c Nhâm Thị Phương - Bí thư Huyện ủy - Chủ tịch HĐND, đ/c Hà Trung Chiến - Phó Bí thư Huyện ủy - Chủ tịch UBND huyện, đã đi kiểm tra công tác chuẩn bị ngày hội tại Thị trấn Mộc Châu và xã Đông Sang.</p>\r\n','\r\n<p style=\"text-align: justify;\">Để đảm bảo cho Ngày hội văn hóa huyện Mộc Châu năm 2015 diễn ra thành công, tạo ấn tượng tốt đẹp với du khách, ngày 30/8 Đ/c Nhâm Thị Phương - Bí thư Huyện ủy - Chủ tịch HĐND, đ/c Hà Trung Chiến - Phó Bí thư Huyện ủy - Chủ tịch UBND huyện, đã đi kiểm tra công tác chuẩn bị ngày hội tại Thị trấn Mộc Châu và xã Đông Sang.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/30 8 2015 bthu2.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Đến thời điểm này mọi công tác chuẩn bị đã cơ bản hoàn tất. Các đơn vị chức năng chuẩn bị đầy đủ các điều kiện cơ sở vật chất cho việc đón tiếp đại biểu, bố trí đủ nhân viên hướng dẫn các đoàn khách ở điểm du lịch; vận động nhân dân 2 thị trấn và các xã tiến hành thu gom rác thải, vệ sinh môi trường, tạo cảnh quan môi trường xanh, sạch, đẹp. Chính quyền địa phương phối hợp với lực lượng công an huyện đảm bảo công tác an ninh, trật tự và đảm bảo an toàn giao thông. Công ty quản lý đô thị Mộc Châu phối hợp với tiểu ban trang trí khánh tiết lắp đặt hệ thống đường điện, ánh sáng…</p>\r\n<p style=\"text-align: justify;\">Sau khi đi kiểm tra thực tế, đ/c Nhâm Thị Phương - Bí thư Huyện ủy - Chủ tịch HĐND nhấn mạnh: Đây là sự kiện quan trọng của huyện trong năm 2015, có quy mô về nội dung, chương trình, số lượng du khách rất lớn, nên các công tác chuẩn bị phải được thực hiện kỹ lưỡng, chu đáo; đồng thời yêu cầu các phòng ban liên quan cần tiến hành triển khai nghiêm túc, kịp thời những công việc còn lại đảm bảo đúng tiến độ. Đồng chí cũng giao trách nhiệm cụ thể cho các đơn vị liên quan nhằm tạo sự phối hợp chặt chẽ về mọi mặt, đảm bảo ngày hội diễn ra thành công tốt đẹp.</p>\r\n<p style=\"text-align: right;\"><strong>Đức Cường</strong></p>','1','0','0','146','2015-08-30 10:07:20','196','','2015-08-30 10:30:17','196','196','2015-08-30 10:30:14','2015-08-30 10:07:20','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_8\\/30 8 2015 bthu2.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','3','0','10','','','1','111','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2075','Đ/c Phạm Đức Chính- Phó chủ tịch UBND huyện Mộc Châu kiểm tra công tác chuẩn bị khai giảng năm học mới tại thị trấn Nông Trường Mộc Châu','d-c-pham-duc-chinh-pho-chu-tich-ubnd-huyen-moc-chau-kiem-tra-cong-tac-chuan-bi-khai-giang-nam-hoc-moi-tai-thi-tran-nong-truong-moc-chau','','<p style=\"text-align: justify;\">Ngày 30/8, Đ.c Phạm Đức Chính- Phó chủ tịch UBND huyện Mộc Châu cùng đoàn công tác của huyện đã đến kiểm tra công tác chuẩn bị khai giảng năm học mới tại trường THCS 8/4, 14/6 và Trường Mầm non Thảo Nguyên trên địa bàn thị trấn Nông Trường Mộc Châu.</p>\r\n','\r\n<p style=\"text-align: justify;\">Ngày 30/8, Đ.c Phạm Đức Chính- Phó chủ tịch UBND huyện Mộc Châu cùng đoàn công tác của huyện đã đến kiểm tra công tác chuẩn bị khai giảng năm học mới tại trường THCS 8/4, 14/6 và Trường Mầm non Thảo Nguyên trên địa bàn thị trấn Nông Trường Mộc Châu.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/30 8 2015 pt.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Thực hiện sự chỉ đạo của ngành giáo dục, các đơn vị trường đã tổ chức các hoạt động đầu năm học mới như: đón học sinh đầu cấp; tìm hiểu chương trình giáo dục; xây dựng cảnh quan sư phạm và môi trường thân thiện trong nhà trường; chăm sóc và phát huy giá trị các di tích lịch sử, văn hóa, cách mạng ở địa phương... Để chuẩn bị cho lễ khai giảng, các nhà trường đã thực hiện chu đáo các điều kiện như: trang trí khánh tiết, chú trọng đầu tư mua sắm trang thiết bị, sửa chữa các công trình, cơ sở vật chất giáo dục để phục vụ tốt cho công tác dạy và học trong năm học 2015 - 2016.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/30 8 2015 pt1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Tại buổi kiểm tra, đ.c Phạm Đức Chính đã đánh giá cao công tác chuẩn bị của các đơn vị trường, đồng thời ghi nhận khó khăn và trả lời những kiến nghị, đề xuất của các nhà trường liên quan đến cơ sở vật chất, đội ngũ giáo viên… Đ/c Phó chủ tịch UBND huyện cũng yêu cầu: trước mắt các nhà trường cần quan tâm tu sửa, đẩy nhanh tiến độ các công trình do nhà trường làm chủ đầu tư, tổ chức vệ sinh trường lớp, trang bị sách giáo khoa, dụng cụ học tập… tạo không khí vui tươi, phấn khởi trong ngày khai giảng và suốt năm học. Riêng phần lễ khai giảng vào ngày 5/9 phải được tổ chức với phần lễ ngắn gọn, trang nghiêm.</p>\r\n<p style=\"text-align: right;\"><strong>Thùy Mai</strong></p>','1','0','0','25','2015-08-30 10:12:01','196','','2015-08-30 10:12:15','196','196','2015-08-30 10:12:05','2015-08-30 10:12:01','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_8\\/30 8 2015 pt.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','5','','','1','92','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2076','Người Mông Pa Khen huyện Mộc Châu chuẩn bị đón Tết độc lập','nguoi-mong-pa-khen-huyen-moc-chau-chuan-bi-don-tet-doc-lap','','<p style=\"text-align: justify;\">Hoà chung không khí tưng bừng phấn khởi của nhân dân cả nước kỷ niệm 70 năm Cách mạng tháng tám thành công và Quốc khánh 2/9, đồng bào các dân tộc  Mộc Châu không những đang háo hức mong chờ ngày Tết độc lập để xuống núi đi chợ, vui Tết mà còn hăng say luyện tập các tiết mục tham dự các hoạt động trong Ngày hội Văn hoá các dân tộc huyện Mộc Châu do huyện tổ chức.</p>\r\n','\r\n<p style=\"text-align: justify;\">Hoà chung không khí tưng bừng phấn khởi của nhân dân cả nước kỷ niệm 70 năm Cách mạng tháng tám thành công và Quốc khánh 2/9, đồng bào các dân tộc Mộc Châu không những đang háo hức mong chờ ngày Tết độc lập để xuống núi đi chợ, vui Tết mà còn hăng say luyện tập các tiết mục tham dự các hoạt động trong Ngày hội Văn hoá các dân tộc huyện Mộc Châu do huyện tổ chức.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/30 8 2015 nh1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Tết độc lập năm nay, người Mông bản Pa Khen thị trấn Nông Trường Mộc Châu, huyện Mộc Châu, tỉnh Sơn La mang đến ngày hội tiếng khèn gọi bạn, trong điệu múa Tha Khềnh với bước nhảy mạnh mẽ của các chàng trai và các cô gái. Vì vậy, đang là ngày mùa bận rộn nhưng các chàng trai, cô gái trong bản vẫn đang tranh thủ thời gian say mê luyện tập. Với họ, vui đón Tết độc lập đã trở thành truyền thống. Họ xuống núi, tụ hội về trung tâm huyện để cùng hân hoan đón Tết độc lập, để tỏ lòng biết ơn Đảng, Bác Hồ, Chính phủ đã đem lại độc lập tự do cho đồng bào cả nước và người Mông.</p>\r\n<p style=\"text-align: justify;\">Để xuống chợ đón Tết, trước đó từ nhiều tháng người phụ nữ trong mỗi gia đình đã chuẩn bị se lanh dệt vải, thêu hoa văn để chuẩn bị cho các thành viên gia đình những bộ trang phục mới nhất, đẹp nhất.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/30 8 2015 nh2.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Đón Tết độc lập năm nay đồng bào Mông ở Mộc Châu vui hơn khi thành quả của 1 năm lao động vất vả là những mùa vàng bội thu đói nghèo đã bị đẩy lui, thay vào đó là cuộc sống ấm no. Xuống chợ vui Tết thay vì phải đi bộ hay đi ngựa như trước kia nhà nào cũng có xe máy phương tiện để đi lại thuận lợi.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/30 8 2015 nh.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Mộc Châu đã rực rỡ sắc Cờ, hoa chào đón Tết độc lập, chắc chắn những điệu múa lời ca ơn Đảng của các chàng trai cô gái Mông sẽ mang đến không khí lễ hội thật sôi động và phấn khởi nơi miền núi vùng cao của Tổ quốc./.</p>\r\n<p style=\"text-align: right;\"><strong>Đức Cường</strong></p>','1','0','0','146','2015-08-30 10:19:35','196','','2015-08-30 10:19:50','196','196','2015-08-30 10:19:40','2015-08-30 10:19:35','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_8\\/30 8 2015 nh.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','9','','','1','74','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2077','Đ/c Trần Anh Tuấn - Ủy viên BTV Tỉnh ủy - Giám đốc công an Tỉnh trao huy hiệu Đảng đợt 2/9 tại huyện Mộc Châu','d-c-tran-anh-tuan-uy-vien-btv-tinh-uy-giam-doc-cong-an-tinh-trao-huy-hieu-dang-dot-2-9-tai-huyen-moc-chau','','<p style=\"text-align: justify;\">Hướng tới kỷ niệm 70 năm Cách mạng tháng Tám thành công và Quốc khánh 2/9, ngày 30/8 đ/c Trần Anh Tuấn- Ủy viên Ban Thường vụ Tỉnh ủy- Giám đốc Công an tỉnh cùng với các đồng chí trong Thường trực Huyện ủy Mộc Châu đã đến dự và trao huy hiệu 60 năm tuổi đảng cho 4 đảng viên là ông Lê Ngọc Lâm, ông Đinh Văn Đãng thuộc Đảng bộ thị trấn Mộc Châu và ông Trần Duy Tương, Trần Văn Thành thuộc Đảng bộ thị trấn Nông Trường Mộc Châu</p>\r\n','\r\n<p style=\"text-align: justify;\">Hướng tới kỷ niệm 70 năm Cách mạng tháng Tám thành công và Quốc khánh 2/9, ngày 30/8 đ/c Trần Anh Tuấn- Ủy viên Ban Thường vụ Tỉnh ủy- Giám đốc Công an tỉnh cùng với các đồng chí trong Thường trực Huyện ủy Mộc Châu đã đến dự và trao huy hiệu 60 năm tuổi đảng cho 4 đảng viên là ông Lê Ngọc Lâm, ông Đinh Văn Đãng thuộc Đảng bộ thị trấn Mộc Châu và ông Trần Duy Tương, Trần Văn Thành thuộc Đảng bộ thị trấn Nông Trường Mộc Châu</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/30 8 2015 dang.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Tại buổi lễ, đ/c Trần Anh Tuấn- Ủy viên Ban Thường vụ Tỉnh ủy- Giám đốc Công an tỉnh đã chúc mừng và ghi nhận những cống hiến của các đồng chí đảng viên cho sự nghiệp xây dựng và bảo vệ Tổ quốc, góp phần vào sự nghiệp phát triển kinh tế- xã hội địa phương.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/30 8 2015 dang1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Các đồng chí đảng viên được trao tặng Huy hiệu Đảng lần này khẳng định tiếp tục phát huy truyền thống, tích cực tham gia công tác, phong trào quần chúng, xây dựng Đảng, xây dựng chính quyền địa phương vững mạnh.Đây là một vinh dự lớn của mỗi đảng viên, đồng thời cũng là trách nhiệm đòi hỏi mỗi đảng viên không ngừng rèn luyện, phấn đấu để thực hiện tốt hơn nữa nhiệm vụ mà Đảng giao phó.</p>\r\n<p style=\"text-align: justify;\">Cũng trong dịp này, Đảng bộ huyện Mộc Châu vinh dự có 50 đồng chí được trao Huy hiệu đảng, trong đó có 25 đồng chí được trao huy hiệu 30 năm tuổi đảng, 4 đồng chí được trao huy hiệu 40 năm tuổi đảng, 10 đồng chí trao huy hiệu 50 năm tuổi đảng, 6 đồng chí được trao huy hiệu 55 năm tuổi đảng, 4 đồng chí được trao huy hiệu 60 năm tuổi đảng và 1 đồng chí được trao huy hiệu 65 năm tuổi đảng.</p>\r\n<p style=\"text-align: justify;\">Một số hình ảnh trao tặng Huy hiệu 60 năm tuổi Đảng đến từng đồng chí:</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/30 8 2015 dang2.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_8/30 8 2015 dang3.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: right;\"><strong>Đức Cường</strong></p>','1','0','0','23','2015-08-30 10:27:59','196','','2015-08-31 01:14:44','196','196','2015-08-31 01:14:18','2015-08-30 10:27:59','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_8\\/30 8 2015 dang1.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','4','0','9','','','1','88','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
