insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2079','V/v đổi lại thẻ BHYT trẻ em dưới 06 tuổi còn hạn sử dụng sau ngày 30/9/2015','v-v-doi-lai-the-bhyt-tre-em-duoi-06-tuoi-con-han-su-dung-sau-ngay-30-9-2015','','<table style=\"width: 485px; height: 152px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"254\">\r\n<p align=\"center\">UBND HUYỆN MỘC CHÂU</p>\r\n<p align=\"center\"><strong>PHÒNG LAO ĐỘNG - TB&amp;XH</strong></p>\r\n<p align=\"center\"> </p>\r\n<strong> </strong> <br clear=\"ALL\" />\r\n<p align=\"center\">Số:<strong> 194</strong>/LĐ-TB&amp;XH</p>\r\n<p align=\"center\">V/v đổi lại thẻ BHYT trẻ em dưới 06 tuổi còn hạn sử dụng sau ngày 30/9/2015.</p>\r\n</td>\r\n<td valign=\"top\" width=\"378\">\r\n<p align=\"center\"><strong>CỘNG HOÀ XÃ HỘI CHỦ NGHĨA VIỆT NAM</strong></p>\r\n<p align=\"center\"><strong>Độc lập - Tự do - Hạnh phúc</strong></p>\r\n<p align=\"center\"> </p>\r\n<strong> </strong> <br clear=\"ALL\" />\r\n<p align=\"center\"><em>Mộc Châu, ngày 27 tháng 8  năm 2015</em></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: justify;\"> </p>\r\n<p style=\"text-align: justify;\">               Kính gửi: UBND các xã, thị trấn</p>\r\n<p style=\"text-align: justify;\" align=\"center\"><strong> </strong></p>\r\n<p style=\"text-align: justify;\">Thực hiện Công văn số 253/BHXH ngày 10/8/2015 của Bảo hiểm xã hội huyện Mộc Châu về việc đổi thẻ BHYT;</p>\r\n<p style=\"text-align: justify;\">Để đảm bảo quyền lợi khám chữa bệnh cho tất cả trẻ em dưới 06 tuổi tham gia BHYT theo quy định tại Quyết định số 1313/QD-BHXH ngày 02/12/2014 về việc ban hành mẫu thẻ bảo hiểm y tế; Quyết định số 1314/QĐ-BHXH ngày 02/12/2014 về việc ban hành mã số ghi trên thẻ bảo hiểm y tế và Công văn số 1449/BHXH-ST ngày 25/4/2015 về việc ghi mã nơi đối tượng sinh sống trên thẻ BHYT. Phòng Lao động - Thương binh &amp; Xã hội huyện Mộc Châu đề nghị UBND các xã, thị trấn chỉ đạo các bản, tiểu khu kiểm tra, rà soát thu hồi thẻ BHYT của tất cả trẻ em dưới 06 tuổi có thẻ BHYT còn hạn sử dụng sau ngày 30/9/2015 <em>(trẻ sinh từ ngày 01/01/2010 trở lại đây)</em> để đổi lại theo mẫu mới, gia hạn thời gian sử dụng và mã vùng đối tượng ưu tiên.</p>\r\n<p style=\"text-align: justify;\"><strong><em> Lưu ý</em></strong><em>:Không thu thẻ BHYT đã in của trẻ sinh từ tháng 01/2015 đến hết tháng 8/2015 của 05 xã, thị trấn thuộc vùng I: Tân Lập, Mường Sang, Phiêng Luông, thị trấn Nông Trường và thị trấn Mộc Châu).</em></p>\r\n<p style=\"text-align: justify;\">Vậy, Phòng Lao động - TB&amp;XH đề nghị UBND các xã, thị trấn khẩn trương thông báo tới các bản, tiểu khu để kịp thời thu hồi và đổi lại thẻ BHYT cho trẻ dưới 06 tuổi; Tổng hợp <em>(theo biểu mẫu đính kèm <a href=\"images/Anh_tin_bai_2015/Van_ban/dinh%20kem%20Cong%20van%20so%20194-LD-TBXH%20ngay%2027.8.2015.xls\"><span style=\"font-size: small; color: #0000ff;\"><strong>Tại đây</strong></span></a>)</em> gửi về phòng Lao động - TB&amp;XH huyện trước ngày <strong>08/9/2015</strong> <em>(đồng thời gửi vào hòm thư điện tử: <a href=\"mailto:phonglaodongmc@gmail.com\">phonglaodongmc@gmail.com</a>)</em>. Nếu đơn vị nào chậm trễ làm ảnh hưởng đến quyền lợi của trẻ thì đơn vị đó hoàn toàn chịu trách nhiệm, trong quá trình thực hiện nếu có vướng mắc phản ánh kịp thời về Phòng Lao động - TB&amp;XH huyện Mộc Châu, trực tiếp đ/c Chi, điện thoại: 022 3866677.</p>\r\n<p style=\"text-align: justify;\"> </p>\r\n<table style=\"width: 487px; height: 119px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"324\">\r\n<p><strong><em> </em></strong></p>\r\n<p><strong><em>Nơi nhận: </em></strong></p>\r\n<p>- Như trên;</p>\r\n<p>- Đ/c PCT (V/X) UBND huyện;</p>\r\n<p>- Lưu VT, LĐ (C 05b).</p>\r\n</td>\r\n<td valign=\"top\" width=\"276\">\r\n<p align=\"center\"><strong>TRƯỞNG PHÒNG</strong></p>\r\n<p align=\"center\"><strong> </strong></p>\r\n<p align=\"center\"><strong> </strong></p>\r\n<p align=\"center\"><strong> </strong></p>\r\n<p align=\"center\"><strong>Nguyễn Hữu Hiệu</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>','','1','0','0','13','2015-08-31 07:47:23','196','','2015-08-31 07:47:37','196','196','2015-08-31 07:47:28','2015-08-31 07:47:23','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','7','','','1','437','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','0','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2080','ANTV đưa tin Mộc Châu trước ngày hội văn hóa các dân tộc','antv-dua-tin-moc-chau-truoc-ngay-hoi-van-hoa-cac-dan-toc','','<p><span style=\"color: #1d1d1d; font-family: Arial, verdana, Helvetica, sans-serif; font-size: 15px; font-style: normal; font-variant: normal; font-weight: bold; letter-spacing: normal; line-height: 21px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none;\">(ANTV) - Năm nay, ngày hội văn hóa các dân tộc tại huyện Mộc Châu sẽ diễn ra trong 2 ngày 31/8 và 1/9.  Cho đến thời điểm này, công tác chuẩn bị các điều kiện cần  thiết cũng như  nội dung hoạt động đang được huyện Mộc Châu tập trung  cao độ để sẵn sàng cho ngày hội lớn.</span></p>\r\n','\r\n<p style=\"text-align: center;\">{youtube}mIUmF8X6i98{/youtube}</p>\r\n<div style=\"font-size: 14px; font-family: Arial, verdana, Helvetica, sans-serif; font-weight: normal; color: #010101; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 22px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">Gần 1 tháng nay, tại Trung tâm văn hóa huyện Mộc Châu, hàng chục diễn viên chuyên và không chuyên đang miệt mài  tập luyện phục vụ chương trình nghệ thuật: “Mộc Châu âm vang bản tình ca” sẽ diễn ra vào tối ngày 1/9.</div>\r\n<div style=\"font-size: 14px; font-family: Arial, verdana, Helvetica, sans-serif; font-weight: normal; color: #010101; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 22px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\"> </div>\r\n<div style=\"font-size: 14px; font-family: Arial, verdana, Helvetica, sans-serif; font-weight: normal; color: #010101; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 22px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">Cho đến thời điểm này, toàn bộ kịch bản chương trình đã được hoàn thiện. Đêm nghệ thuật ngày hội văn hóa các dân tộc năm nay sẽ có màn bắn pháo bông đặc sắc. Ngoài ra, để tái hiện những nét văn hóa đặc trưng, đặc sắc của các dân tộc, Ban tổ chức đã huy động gần 200 diễn viên cộng đồng đường phố tập luyện từ hàng chục ngày nay ở cơ sở.</div>\r\n<div style=\"font-size: 14px; font-family: Arial, verdana, Helvetica, sans-serif; font-weight: normal; color: #010101; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 22px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\"> <span class=\"Apple-converted-space\"> </span></div>\r\n<div style=\"font-size: 14px; font-family: Arial, verdana, Helvetica, sans-serif; font-weight: normal; color: #010101; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 22px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">Bà Phạm Thị Lợi – Giám đốc Trung tâm văn hóa huyện Mộc Châu cho biết: Chúng tôi đã huy động trên 200 diễn viên tham gia. Đêm hội chúng tôi chủ yếu giới thiệu đặc sắc vê văn  hóa các dân tộc Thái, Mông, Dao, Mường của huyện Mộc Châu. Ngoài ra chương  trình văn hóa cộng đồng đường  phố chúng tôi muốn  tái hiện lại những hoạt cảnh của người Mông và các lễ hội  của người Thái.</div>\r\n<div style=\"font-size: 14px; font-family: Arial, verdana, Helvetica, sans-serif; font-weight: normal; color: #010101; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 22px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\"> </div>\r\n<div style=\"font-size: 14px; font-family: Arial, verdana, Helvetica, sans-serif; font-weight: normal; color: #010101; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 22px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">Công tác chuẩn bị các điều kiện vật chất, hậu cần kỹ thuật cũng đang được huyện Mộc Châu tập trung  cao độ. Toàn bộ hệ thống lán trại, được lắp ráp đồng bộ. Có kế hoạch chi tiết tổ chức hiệp đồng chặt chẽ với các lực lượng đảm bảo an ninh - trật tự trước, trong và sau thời gian diễn ra Ngày hội. Xây dựng phương án cụ thể, bố bí lực lượng, phương tiện đảm bảo công tác phòng chống cháy, đảm bảo an toàn giao thông. Phân công lực lượng duy trì nghiêm các chế độ trực sẵn sàng chiến đấu để kịp thời xử lý các tình huống có thể xảy ra.</div>\r\n<div style=\"font-size: 14px; font-family: Arial, verdana, Helvetica, sans-serif; font-weight: normal; color: #010101; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 22px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\"> <span class=\"Apple-converted-space\"> </span></div>\r\n<div style=\"font-size: 14px; font-family: Arial, verdana, Helvetica, sans-serif; font-weight: normal; color: #010101; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 22px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">Theo ông Trần Xuân Việt - Trưởng phòng Văn hóa thể thao và du lịch Mộc Châu: Năm nay có nét mới đó là huyện đã phát động cuộc thi sáng tác bảnh đẹp  Mộc Châu và sáng tác biểu trưng cua huyện. nó rất phù hợp với việc Mộc Châu vừa được công nhận là Khu du lịch quốc gia. Ngoài ra các hoạt động trong ngày hội cũng hướng nhiều hơn tới du khách. Du khách sẽ được trực tiếp tham gia các hoạt động như tham gia cá trò chơi dân tộc.</div>\r\n<div style=\"font-size: 14px; font-family: Arial, verdana, Helvetica, sans-serif; font-weight: normal; color: #010101; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 22px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\"> <span class=\"Apple-converted-space\"> </span></div>\r\n<div style=\"font-size: 14px; font-family: Arial, verdana, Helvetica, sans-serif; font-weight: normal; color: #010101; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 22px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px;\">Ngày hội văn hóa các dân tộc huyện Mộc Châu sẽ tập trung chủ yếu  trong 2 ngày: Từ ngày 31/8 đến hết ngày 1/9. Ngày hội sẽ là dịp để du khách trải nghiệm và thưởng thức giá trị văn hóa độc đáo của các dân tộc huyện Mộc Châu. Đồng thời đây còn là dịp để quảng  bá, giới thiệu về tiềm năng của Khu du lịch quốc gia Mộc Châu tới du khách thập phương.</div>\r\n<div style=\"font-size: 14px; font-family: Arial,verdana,Helvetica,sans-serif; font-weight: normal; color: #010101; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 22px; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; text-align: right;\"><strong>Nguồn ANTV</strong></div>','1','0','0','146','2015-09-01 08:54:04','196','','2015-09-01 08:54:15','196','196','2015-09-01 08:54:08','2015-09-01 08:54:04','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_8\\/1 9 2015 antv.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','7','','','1','81','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2081','Đắm mình vào Lễ hội đường phố Mộc Châu trong ngày Tết Độc lập','dam-minh-vao-le-hoi-duong-pho-moc-chau-trong-ngay-tet-doc-lap','','<p style=\"text-align: justify;\">QV&amp;CB có thể cảm nhận được hết cái không khí tưng bừng náo nhiệt của Lễ hội đường phố trong ngày Tết Độc lập của bà con các dân tộc huyện Mộc Châu, tỉnh Sơn La. Tất cả mọi ngưòi không phân biệt gìa trẻ, gái trai, thành phần dân tộc... cùng hòa vào nhau, cùng nhảy múa những điệu múa đặc sắc nhất của dân tộc mình thể hiện tình yêu thương, sự đoàn kết của nhân dân các dân tộc Việt Nam trong ngày Quốc khánh.</p>\r\n','\r\n<p style=\"text-align: justify;\">QV&amp;CB có thể cảm nhận được hết cái không khí tưng bừng náo nhiệt của Lễ hội đường phố trong ngày Tết Độc lập của bà con các dân tộc huyện Mộc Châu, tỉnh Sơn La. Tất cả mọi ngưòi không phân biệt gìa trẻ, gái trai, thành phần dân tộc... cùng hòa vào nhau, cùng nhảy múa những điệu múa đặc sắc nhất của dân tộc mình thể hiện tình yêu thương, sự đoàn kết của nhân dân các dân tộc Việt Nam trong ngày Quốc khánh.</p>\r\n<p style=\"text-align: justify;\">Lễ hội đường phố năm 2015 có các đoàn nghệ nhân dân tộc Mông, Thái, Mường Dao, Kinh đem đến cho Ngày hội Văn hóa các dân tộc huyện Mộc Châu những sắc mầu văn hóa đặc trưng riêng đầy hấp dẫn và ấn tượng. Thu hút sự quan tâm chú ý của đông đảo người dân và du khách đến Mộc Châu.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/2 9 2015 lh2.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Gây được sự chú ý nhất là đoàn nghệ nhân dân tộc Mông ở Tiểu khu Pa khen I, Pa khen III thị trấn Nông Trường Mộc Châu. Những cô gái, chàng trai Mông thể hiện sự duyên dáng, khỏe mạnh trong những điệu múa dân tộc. Những người lớn tuổi lại thể hiện sự điêu luyện, tài tình trong tiếng khèn dặt dìu. Đặc biệt hơn cả là đoàn nghệ nhân dân tộc Mông đã tái hiện lại hình ảnh người phụ nữ dắt ngựa, chở chồng trong những đêm hội.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/2 9 2015 lh3.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Không chỉ có đồng bào dân tộc Mông, các nghệ nhân dân tộc Thái, Dao, Mường và cả dân tộc Kinh sinh sống trên cao nguyên Mộc Châu cũng mang đến lễ hội những điều đặc sắc nhất, tinh túy nhất của văn hóa dân tộc mình. Không hấp dẫn, không thu hút sao được khi được tận mắt nhìn thấy các cô gái Thái, Mường dịu dàng, uyển chuyển, các chàng trai, cô gái Dao rộn ràng trong điệu múa truyền thống. Tất cả như hòa quyện với nhau tạo nên một bức tranh văn hóa đầy mầu sắc và mới lạ. Cũng chính bởi cái đẹp, cái tình đoàn kết đó của bà con các dân tộc đã lan tỏa đến cả những du khách đến với Mộc Châu trong dịp lễ hội này.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/2 9 2015 lh.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Tết độc lập ở Mộc Châu đã trở thành truyền thống nhưng mỗi năm lại có những nét đặc sắc riêng. Để rồi, tan hội mỗi người ra về vẫn còn lại ấn tượng, sự lưu luyến trong lòng về tình cảm, sự đoàn kết của bà con các dân tộc Mộc Châu, và họ lại hẹn nhau Tết độc lập năm sau lại xuống chợ, hòa cùng nhau trong tiếng hát, tiếng khèn.  </p>\r\n<p style=\"text-align: right;\"><strong>Thùy Mai</strong></p>','1','0','0','146','2015-09-02 10:45:56','196','','2015-09-02 10:50:29','196','196','2015-09-02 10:46:01','2015-09-02 10:45:56','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_9\\/2 9 2015 lh.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','6','','','1','105','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2082','Nổi bật cuộc thi sáng tác biểu trưng Mộc Châu ảnh đẹp Mộc Châu xưa và nay','noi-bat-cuoc-thi-sang-tac-bieu-trung-moc-chau-anh-dep-moc-chau-xua-va-nay','','<p style=\"text-align: justify;\">Cuộc thi sáng tác biểu trưng (logo) Mộc Châu, ảnh đẹp Mộc Châu xưa và nay là điểm mới, nổi bật trong chuỗi các hoạt động của Ngày hội văn hóa các dân tộc Mộc Châu. Cuộc thi đã thu hút 63 tác giả trong nước với gần 100 biểu trưng và trên 200 bức ảnh đẹp về mảnh đất, con người, văn hoá, du lịch Mộc Châu.</p>\r\n','\r\n<p style=\"text-align: justify;\">Cuộc thi sáng tác biểu trưng (logo) Mộc Châu, ảnh đẹp Mộc Châu xưa và nay là điểm mới, nổi bật trong chuỗi các hoạt động của Ngày hội văn hóa các dân tộc Mộc Châu. Cuộc thi đã thu hút 63 tác giả trong nước với gần 100 biểu trưng và trên 200 bức ảnh đẹp về mảnh đất, con người, văn hoá, du lịch Mộc Châu.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/2 9 2015 lg.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Ban tổ chức cuộc thi đã chọn 100 bức ảnh đẹp và 5 biểu trưng (logo) xuất sắc nhất để trưng bày triển lãm tại nhà văn hóa huyện Mộc Châu. Là điểm nhấn trong ngày hội văn hóa các dân tộc Mộc Châu năm 2015 nên triển lãm ảnh, biểu trưng (logo) Mộc Châu thu hút được sự quan tâm của đông đảo du khách… Những biểu trưng (logo), bức ảnh mang hơi thở của cuộc sống hàng ngày làm cho người xem thấy được những điều bình dị mà thân thuộc trên cao nguyên Mộc Châu tươi đẹp. Đặc biệt những bức ảnh Mộc Châu xưa được các tác giả sưu tầm mang đến hình ảnh của cuộc sống, hoạt động lao động sản xuất, nét sinh hoạt văn hoá các dân tộc Mộc Châu, từ khoảng trước những năm 1986 đã mang đến cho khán giả những cảm xúc đặc biệt.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/2 9 2015 lg1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Cao nguyên Mộc Châu đẹp, quyến rũ bởi vẻ đẹp được thiên nhiên ưu đãi, bởi những con người hồn hậu tạo nên cảm hứng vô tận cho những nhiếp ảnh gia sẵn sàng tác nghiệp. Mộc Châu qua góc nhìn của người nghệ sĩ  đã phản ánh chân thực mảnh đất, con người Mộc Châu trong thời kỳ đổi mới. Những bức ảnh mang hơi thở của cuộc sống, là hình ảnh người cha dạy con thổi khèn, hình ảnh cô gái thái dịu dàng e ấp bên nhành ban trắng, hình ảnh lao động sản xuất của bà con các dân tộc Mộc Châu.</p>\r\n<p style=\"text-align: justify;\">Cuộc thi sáng tác biểu trưng Mộc Châu, ảnh đẹp Mộc Châu xưa và nay được phát động từ tháng 7/2015, đã thu hút được sụ tham gia của đông đảo tác giả, nghệ sĩ nhiếp ảnh có tình yêu mãnh liệt và sự đam mê với mảnh đất, con người Mộc Châu. Nhờ sự sáng tạo của các tác giả những biểu trưng đã phản ánh được những nét tiêu biểu, đặc trưng và nổi bật nhất của huyện Mộc Châu, đặc biệt là các yếu tố về văn hóa, con người, tài nguyên, thiên nhiên Mộc Châu. Làm được điều đó không chỉ những con người sinh ra, lớn lên gắn bó với cao nguyên Mộc Châu mà có cả những tác giả ở khắp mọi miền tổ quốc nhưng lại dành cho Mộc Châu những tình cảm đặc biệt.</p>\r\n<p style=\"text-align: justify;\">Cuộc thi sáng tác biểu trưng (logo) Mộc Châu, ảnh đẹp Mộc Châu xưa và nay đã thành công tốt đẹp mặc dù không chọn được biểu trưng (logo) Mộc Châu, không tìm được bức ảnh đạt giải nhất trong đợt này nhưng đã cho thấy sức lan tỏa mạnh mẽ để lại ấn tượng sâu sắc. Đêm 31/8 Ban tổ chức đã tổ chức Lễ công bố kết quả và trao giải cho 5 tác giả sáng tác biểu trưng xuất sắc nhất, 3 tác giả sưu tầm ảnh xưa đẹp nhất và trao 2 giải nhì, 3 giải 3 và các giải khuyến khích cho các nhiếp ảnh gia có những bức ảnh đẹp nhất về Mộc Châu nay.</p>\r\n<p style=\"text-align: justify;\"><em>Một số hình ảnh:</em></p>\r\n<p style=\"text-align: justify;\"><em><img src=\"images/Anh_tin_bai_2015/Thang_9/3 9 2015 logo.jpg\" border=\"0\" alt=\"\" /></em></p>\r\n<p style=\"text-align: justify;\"> </p>\r\n<p style=\"text-align: right;\"><strong><img src=\"images/Anh_tin_bai_2015/Thang_9/3 9 2015 logo1.jpg\" border=\"0\" alt=\"\" /></strong></p>\r\n<p style=\"text-align: right;\"> </p>\r\n<p style=\"text-align: left;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/3 9 2015 logo2.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: left;\"><strong><img src=\"images/Anh_tin_bai_2015/Thang_9/3 9 2015 logo3.jpg\" border=\"0\" alt=\"\" /><br /></strong></p>\r\n<p style=\"text-align: left;\"><strong><img src=\"images/Anh_tin_bai_2015/Thang_9/3 9 2015 logo4.jpg\" border=\"0\" alt=\"\" /></strong></p>\r\n<p style=\"text-align: left;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/3 9 2015 logo5.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: left;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/3 9 2015 logo6.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: left;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/3 9 2015 logo7.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: right;\"><strong>Thùy Mai</strong></p>','1','0','0','146','2015-09-02 10:53:27','196','','2015-09-03 02:35:07','196','196','2015-09-03 02:34:57','2015-09-02 10:53:27','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_9\\/3 9 2015 logo7.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','4','0','5','','','1','170','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2083','Lễ hội chọi trâu','le-hoi-choi-trau','','<p style=\"text-align: justify;\">Nằm trong chuỗi các hoạt động của Ngày hội Văn hóa các dân tộc Mộc Châu năm 2015, trong 2 ngày mùng 1 và 2/9, tại xã Lóng Sập đã diễn ra Lễ hội chọi trâu. Đây là lần đầu tiên huyện Mộc Châu tổ chức Lễ hội chọi trâu, thu hút hơn 5000 khán giả tham dự và cổ vũ.</p>\r\n','\r\n<p style=\"text-align: justify;\">Nằm trong chuỗi các hoạt động của Ngày hội Văn hóa các dân tộc Mộc Châu năm 2015, trong 2 ngày mùng 1 và 2/9, tại xã Lóng Sập đã diễn ra Lễ hội chọi trâu. Đây là lần đầu tiên huyện Mộc Châu tổ chức Lễ hội chọi trâu, thu hút hơn 5000 khán giả tham dự và cổ vũ.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/2 9 2015 tr.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Lễ hội không chỉ có giá trị văn hoá, tín ngưỡng, độc đáo mà còn thể hiện tình đoàn kết giữa các dân tộc anh em trên địa bàn tỉnh Sơn La và thắt chặt hơn nữa tình hữu nghị đặc biệt giữa nhân dân hai nước Việt – Lào. Lễ hội chọi trâu được tổ chức cũng chính là mong muốn của bà con vào 1 năm mới sản xuất thắng lợi, mùa màng bội thu. Khuyến khích bà con nhân dân thi đua chăn nuôi sản xuất phát triển đàn đại gia súc, bởi con trâu là đầu cơ nghiệp.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/2 9 2015 tr1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Với những nét văn hoá đặc sắc lễ hội đã tạo bầu không khí vui tươi phấn khởi cho bà con nhân dân địa phương và du khách gần xa.</p>\r\n<p style=\"text-align: justify;\">Kết thúc hai ngày thi đấu, giải nhất đã thuộc về trâu của anh Mùa A So - xã Đông Tải – huyện Mai Sơn, giải nhì thuộc về trâu của anh Lê Văn Diễm – xã Tường Thượng – huyện Phù Yên.</p>\r\n<p style=\"text-align: right;\"><strong>Anh Đức</strong></p>','1','0','0','146','2015-09-02 11:01:40','196','','2015-09-02 11:02:09','196','0','0000-00-00 00:00:00','0000-00-00 00:00:00','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_9\\/2 9 2015 tr1.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','4','','','1','237','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2084','Tết độc lập nhân lên tình hữu nghị Việt - Lào','tet-doc-lap-nhan-len-tinh-huu-nghi-viet-lao','','<p style=\"text-align: justify;\">Hoà chung không khí của nhân dân khắp mọi vùng, mọi miền của đất nước đón chào Tết Độc lập tại khu vực biên giới của huyện Mộc Châu tỉnh Sơn La, trong ngày Quốc khánh khoảng cách biên giới dường như bị xóa nhòa, khi người Việt và người Lào cùng tụ hội, chung vui trong không khí đón mừng Tết độc lập nơi biên cương của tổ quốc.</p>\r\n','\r\n<p style=\"text-align: justify;\">Hoà chung không khí của nhân dân khắp mọi vùng, mọi miền của đất nước đón chào Tết Độc lập tại khu vực biên giới của huyện Mộc Châu tỉnh Sơn La, trong ngày Quốc khánh khoảng cách biên giới dường như bị xóa nhòa, khi người Việt và người Lào cùng tụ hội, chung vui trong không khí đón mừng Tết độc lập nơi biên cương của tổ quốc.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/2 9 2015 ho1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Niềm vui đón Tết độc lập năm này của đồng bào vùng cao biên giới nơi cửa khẩu Quốc gia Lóng Sập, xã  Lóng Sập huyện Mộc Châu tỉnh Sơn La lại được nhân lên gấp đôi khi trong những ngày này rất đông người dân bên biên giới Việt - Lào lại cùng tụ hội để cùng vui đón Tết độc lập. Chị Mái Chú Vàng là một trong những người Mông ở cụm bản Pa Háng Huổi Hiềng thuộc huyện Sốp Bâu tỉnh Hủa Phăn đã sang Lóng Sập huyện Mộc Châu cùng hòa chung niềm hân hoan của người dân nơi đay trong ngày Quốc Khánh.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/2 9 2015 ho.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Xã Lóng Sập huyện Mộc Châu, tỉnh  Sơn La có rất động đồng bào dân tộc Mông cư trú. Xã cách trung tâm huyện Mộc Châu 30 km, đã từ lâu đây đã là một trung tâm mua bán, giao lưu, trao đổi hàng hóa giữa đồng bào các dân tộc và nhân dân 2 bên biên giới.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/2 9 2015 ho2.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Đón Tết Độc lập năm nay xã Lóng Sập huyện Mộc Châu đã tổ chức nhiều hoạt động phong phú như: Hoạt động thương mại và ẩm thực độc đáo của vùng cao để phục vụ người dân địa phương và du khách tham quan. Cùng với đó xã Lóng Sập thời tổ chức hoạt động văn hóa văn nghệ, các trò chơi dân gian để tạo không khí vui tươi phấn khởi trong các tầng lớp nhân dân</p>\r\n<p style=\"text-align: justify;\">Việt Nam và Lào là hai nước láng giềng có nhiều nét tương đồng về văn hóa và đã chung tay viết lên những trang lịch sử hào hùng của hai dân tộc. Tình hữu nghị, đoàn kết giữa hai Đảng, hai nước Việt Nam - Lào và sự gắn bó thủy chung, keo sơn giữa dân tộc Việt Nam và nhân dân các bộ tộc Lào được Chủ tịch Hồ Chí Minh và Chủ tịch Cay-xỏn Phom-vi-hản gây dựng, được các thế hệ lãnh đạo kế tục của hai Đảng, hai nước, cùng nhân dân hai nước quý trọng, nâng niu và dày công vun đắp.</p>\r\n<p style=\"text-align: justify;\">Mối quan hệ, tình hữu nghị 2 quốc gia, 2 dân tộc Việt - Lào bền vững đó không phải từ những điều gì to lớn mà được bắt đầu từ những quan hệ giản đơn, niềm vui ngày Tết Độc lập của Dân tộc Việt cũng được người dân các bộ tộc Lào coi là niềm vui của của chính mình, họ cũng hân hoan đón Tết Độc lập như gắn chặt thêm tình đoàn kết giữa 2 dân tộc để cùng xây dựng vùng biên ổn định và phát triển...</p>\r\n<p style=\"text-align: right;\"><strong>Đức Cường</strong></p>','1','0','0','146','2015-09-02 12:16:11','196','','2015-09-02 12:16:28','196','196','2015-09-02 12:16:18','2015-09-02 12:16:11','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_9\\/2 9 2015 ho1.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','3','','','1','88','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2085','Hội hoa - Du lịch Mộc Châu 2015','hoi-hoa-du-lich-moc-chau-2015','','<p style=\"text-align: justify;\">Sáng 1/9, Ban tổ chức Ngày hội văn hóa các dân tộc huyện Mộc Châu năm 2015 khai mạc Hội hoa – Du lịch Mộc Châu lần thứ 2/2015. Đây là một trong những hoạt động được đông đảo nhân dân và du khách quan tâm tại Ngày hội văn hóa các dân tộc năm nay.</p>\r\n','\r\n<p style=\"text-align: justify;\">Sáng 1/9, Ban tổ chức Ngày hội văn hóa các dân tộc huyện Mộc Châu năm 2015 khai mạc Hội hoa – Du lịch Mộc Châu lần thứ 2/2015. Đây là một trong những hoạt động được đông đảo nhân dân và du khách quan tâm tại Ngày hội văn hóa các dân tộc năm nay.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/3 9 2015 hoa.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Dự khai mạc Hội hoa - Du lịch có đồng chí Bùi Đức Hải – Phó chủ tịch Ủy ban Nhân dân tỉnh Sơn La, Đ/c Hà Trung Chiến – Phó Bí thư Huyện ủy – Chủ tịch UBND huyện.  Hội hoa – Du lịch 2015 giới thiệu tới đại biểu và du khách 25 gian hàng của 15 đơn vị, doanh nghiệp trên địa bàn huyện với những không gian hoa và du lịch Mộc Châu rực rỡ đầy màu sắc. Bên cạnh các gian hàng trưng bày các sản phẩm hoa, đại biểu và du khách còn được tham quan các gian hàng trung bày giới thiệu sản phẩm nông nghiệp, các trang phục dân tộc, công cụ lao động, sản xuất đặc trưng của đồng bào các dân tộc Mộc Châu.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/3 9 2015 hoa1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Hội hoa - du lịch năm nay nhằm tiếp tục tôn vinh những người trồng hoa, người làm du lịch, các tổ chức, cá nhân có các sản phẩm nông nghiệp nổi tiếng và đặc trưng tại cao nguyên Mộc Châu. Qua đó, động viên và khuyến khích các doanh nghiệp, tổ chức, cá nhân đẩy mạnh hơn nữa phong trào trồng hoa, phát triển các sản phẩm du lịch; phát triển nông nghiệp tương hỗ cho phát triển du lịch; bảo tồn và phát huy bản sắc văn hóa các dân tộc; tích cực xây dựng những sản phẩm du lịch độc đáo và hấp dẫn.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/3 9 2015 hoa2.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Hội hoa - Du lịch được tổ chức hàng năm nhằm góp phần đẩy mạnh tuyên truyền, quảng bá, giới thiệu về một Mộc Châu năng động, giàu tiềm năng; xây dựng thương hiệu cho các sản phẩm nông nghiệp và du lịch của Mộc Châu, đồng thời đẩy mạnh các hoạt động xúc tiến, thu hút đầu tư vào địa bàn.</p>','1','0','0','146','2015-09-03 03:28:22','196','','2015-09-03 03:28:37','196','196','2015-09-03 03:28:26','2015-09-03 03:28:22','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_9\\/3 9 2015 hoa.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','2','','','1','102','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2086','V/v Tham gia giải thưởng “Vô lăng vàng” lần thứ 3 năm 2015','v-v-tham-gia-giai-thuong-vo-lang-vang-lan-thu-3-nam-2015','','<table style=\"width: 489px; height: 147px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td style=\"text-align: center;\" valign=\"top\" width=\"271\">\r\n<p align=\"center\"><span style=\"font-size: x-small;\">UBND HUYỆN MỘC CHÂU</span></p>\r\n<p align=\"center\"><span style=\"font-size: x-small;\"><strong>BAN AN TOÀN GIAO THÔNG</strong></span></p>\r\n<p><span style=\"font-size: x-small;\"><strong>                                                </strong></span></p>\r\n<p><span style=\"font-size: x-small;\"><strong> </strong></span></p>\r\n<p><span style=\"font-size: x-small;\">     Số: <strong>24/</strong>BATGT</span></p>\r\n<p align=\"center\"><span style=\"font-size: x-small;\">V/v Tham gia giải thưởng “Vô lăng vàng” lần thứ 3 năm 2015</span></p>\r\n</td>\r\n<td style=\"text-align: center;\" valign=\"top\" width=\"412\">\r\n<p align=\"center\"><span style=\"font-size: x-small;\"><strong>CỘNG HOÀ XÃ HỘI CHỦ NGHĨA VIỆT NAM</strong></span></p>\r\n<p align=\"center\"><span style=\"font-size: x-small;\">Độc lập - Tự do - Hạnh phúc</span></p>\r\n<p><span style=\"font-size: x-small;\"><em>           </em></span></p>\r\n<p><span style=\"font-size: x-small;\"><em> </em></span></p>\r\n<p><span style=\"font-size: x-small;\"><em>                Mộc Châu,  ngày 24  tháng 7  năm 2015</em></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>                        </p>\r\n<p>                              Kính gửi:</p>\r\n<p>                                            - Phòng Văn hóa và Thông tin;</p>\r\n<p>                                            - Đài truyền thanh - Truyền hình huyện;</p>\r\n<p>                                            - UBND các xã, thị trấn.</p>\r\n<p> </p>\r\n<p align=\"center\"><strong> <br /></strong></p>\r\n<p>Thực hiện Công văn số 50/BATGT-VP ngày 15/7/2015 của Ban ATGT tỉnh về việc tham gia giải thưởng “Vô lăng vàng” lần thứ 3 năm 2015.</p>\r\n<p>Nhằm tôn vinh tôn vinh các lái xe, các doanh nghiệp vận tải có nhiều đóng góp trong công tác bảo đảm trật tự an toàn giao thông; nâng cao ý thức của toàn dân, đội ngũ lái xe trong việc chấp hành pháp luật trật tự an toàn giao thông, phòng tránh tai nạn giao thông, góp phần thực hiện mục tiêu giảm tai nạn giao thông từ 5-10% năm 2015.</p>\r\n<p> </p>\r\n<p>Ban An toàn giao thông huyện đề nghị các cơ quan, đơn vị, UBND các xã, thị trấn tổ chức tuyên truyền Thể lệ giải thưởng “Vô lăng vàng” năm 2015 để các doanh nghiệp, đơn vị vận tải, lái xe tham gia:</p>\r\n<table style=\"width: 475px; height: 24px;\" border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td style=\"text-align: center;\"><span style=\"font-size: medium;\"><strong>Thể lệ</strong></span></td>\r\n<td style=\"text-align: center;\"><a href=\"images/Anh_tin_bai_2015/Van_ban/The%20le%20VLV%202015.doc\"><span style=\"font-size: medium; color: #0000ff;\"><strong>Tải về tại đây</strong></span></a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><strong> </strong></p>\r\n<p><strong>1.</strong> Phòng Văn hóa và Thông tin: Tuyên truyền Thể lệ giải thưởng “Vô lăng vàng” năm 2015 trên Cổng thông tin điện tử của huyện.</p>\r\n<p><strong>2.</strong> Đài Truyền thanh - Truyền hình: Tổ chức tuyên truyền trên hệ thống truyền thanh, Truyền hình của huyện nội dung Thể lệ giải thưởng “Vô lăng vàng” lần thứ 3 năm 2015.</p>\r\n<p><strong>3.</strong> UBND các xã, thị trấn: Thông báo đến các doanh nghiệp, hợp tác xã vận tải đóng trên địa bàn về giải thưởng “Vô lăng vàng” năm 2015 để các dơn vị tổ chức tham gia.</p>\r\n<p>Ban ATGT huyện gửi đến các cơ quan, đơn vị Quyết định và Thể lệ giải thưởng “Vô lăng vàng” lần thứ 3 năm 2015 của Ủy ban An toàn giao thông Quốc gia để tổ chức tuyên truyền.</p>\r\n<p> </p>\r\n<p>Ban An toàn giao thông huyện đề nghị các cơ quan nêu trên quan tâm, triển khai thực hiện./.</p>\r\n<p> </p>\r\n<table style=\"width: 485px; height: 137px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"248\">\r\n<p><strong><em>Nơi nhận:</em></strong></p>\r\n<p>- Ban ATGT tỉnh Sơn La;</p>\r\n<p>- TTHU, HĐND huyện;</p>\r\n<p>- Chủ tịch UBND huyện;</p>\r\n<p>- Lưu Ban ATGT,T,20b.</p>\r\n</td>\r\n<td valign=\"top\" width=\"375\">\r\n<p align=\"center\"><strong>KT. TRƯỞNG BAN</strong></p>\r\n<p align=\"center\"><strong>PHÓ TRƯỞNG BAN THƯỜNG TRỰC</strong></p>\r\n<p align=\"center\"><strong> </strong></p>\r\n<p align=\"center\"><strong> </strong></p>\r\n<p align=\"center\"><strong>TRƯỞNG PHÒNG KINH TẾ VÀ HẠ TẦNG</strong></p>\r\n<p align=\"center\"><strong>Lò Minh Hiệp</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>','','1','0','0','13','2015-09-03 07:06:37','196','','2015-09-03 07:06:49','196','196','2015-09-03 07:06:42','2015-09-03 07:06:37','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','6','','','1','414','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','0','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2087','Ban chỉ huy quân sự huyện Mộc Châu chuyển tiền thăm hỏi của quân khu II cho gia đình bị thiệt hại bão số 1','ban-chi-huy-quan-su-huyen-moc-chau-chuyen-tien-tham-hoi-cua-quan-khu-ii-cho-gia-dinh-bi-thiet-hai-bao-so-1','','<p style=\"text-align: justify;\">Nhân kỷ niệm 70 năm Quốc khánh nước cộng hòa xã hội chủ nghĩa Việt Nam,  ngày 1/9 Ban chỉ huy Quân sự huyện Mộc Châu đã chuyển trao 3 triệu đồng tiền thăm hỏi của Quân khu II cho gia đình anh Hà Xuân Thắng - chị Lò Thị Hòa bị mưa lũ cuốn trôi mất tài sản và người con trai 6 tuổi vào ngày 24/6.</p>\r\n','\r\n<p style=\"text-align: justify;\">Nhân kỷ niệm 70 năm Quốc khánh nước cộng hòa xã hội chủ nghĩa Việt Nam,  ngày 1/9 Ban chỉ huy Quân sự huyện Mộc Châu đã chuyển trao 3 triệu đồng tiền thăm hỏi của Quân khu II cho gia đình anh Hà Xuân Thắng - chị Lò Thị Hòa bị mưa lũ cuốn trôi mất tài sản và người con trai 6 tuổi vào ngày 24/6.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 qs.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Với sự đồng viên của Ban chỉ huy Quân sự huyện Mộc Châu cùng với các cấp, các ngành và chính quyền địa phương sẽ giúp cho gia đình anh Hà Xuân Thắng và chị Lò Thị Hòa dựng lại nhà mới, ổn định cuộc sống, yên tâm công tác tại xã vùng 3 biên giới Chiềng Khừa.</p>\r\n<p style=\"text-align: right;\"><strong>Đức Cường</strong></p>','1','0','0','9','2015-09-04 02:10:56','196','','2015-09-04 02:11:20','196','196','2015-09-04 02:11:10','2015-09-04 02:10:56','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_9\\/4 9 2015 qs.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','2','','','1','53','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2088','Khai mạc lễ hội hết chá năm 2015','khai-mac-le-hoi-het-cha-nam-2015','','<p style=\"text-align: justify;\">Nằm trong chuỗi các hoạt động của Ngày hội văn hóa các dân tộc huyện Mộc Châu năm 2015, sáng 1/9 tại Khu du lịch rừng thông Bản Áng, xã Đông Sang đã tổ chức Lễ hội Hết Chá cùng nhiều hoạt động văn hóa, trò chơi dân gian, các môn thể thao dân tộc hấp dẫn.</p>\r\n','\r\n<p style=\"text-align: justify;\">Nằm trong chuỗi các hoạt động của Ngày hội văn hóa các dân tộc huyện Mộc Châu năm 2015, sáng 1/9 tại Khu du lịch rừng thông Bản Áng, xã Đông Sang đã tổ chức Lễ hội Hết Chá cùng nhiều hoạt động văn hóa, trò chơi dân gian, các môn thể thao dân tộc hấp dẫn. Tới dự có đồng chí Bùi Đức Hải – Phó Chủ tịch UBND tỉnh Sơn La, đồng chí Hà Trung Chiến – Phó Bí thư huyện ủy– Chủ tịch UBND huyện cùng các đồng chí trong thường trực huyện ủy, HĐND, UBND, các cơ quan, ban ngành đoàn thể của huyện.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hc.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Lễ Hội là ngày hội đoàn kết các gắn bó cộng đồng, mang tính nhân văn sâu sắc, thể hiện cuộc sống bình dị mà thanh bình của người dân với thiên nhiên. Với 2 phần rõ nét là phần lễ và phần hội. Phần lễ thể hiện những khao khát của người dân về một cuộc sống hướng thiện, thanh bình và mong muốn có được cuộc sống đoàn kết, ấm no hạnh phúc đồng thời phê phán những thói hư tật xấu, khích lệ những cái hay, cái đẹp trong cuộc sống đời thường ....phần hội mô phỏng lại cuộc sống bình dị thường ngày của đồng bào dân tộc Thái trong suốt quá trình dựng bản, dựng mường và xây dựng đời sống mới. Bên cạnh đó, người xem và du khách còn có cơ hội được đắm mình trong tiếng trống, tiếng chiêng sôi động, tiếng sáo Pí Mun dập dìu, trong vòng xòe đoàn kết của bà con dân tộc Thái xã Đông Sang. Cũng tại đây du khách còn được tham gia vào các trò chơi dân gian lý thú như đi cà kheo, tung còn, đi cầu kiều…</p>\r\n<p style=\"text-align: justify;\">Lễ hội hết chá 2015 thành công không chỉ để lại ấn tượng sâu sắc trong lòng du khách mà còn góp phần tôn vinh các hoạt động văn hoá dân gian, văn hoá cộng đồng, đồng thời giới thiệu, quảng bá đến du khách gần xa về điểm du lịch sinh thái Rừng thông bản Áng đầy thơ mộng và hấp dẫn.</p>\r\n<p style=\"text-align: right;\"><strong>Thanh Bình</strong></p>','1','0','0','146','2015-09-04 02:15:49','196','','2015-09-04 02:16:02','196','196','2015-09-04 02:15:53','2015-09-04 02:15:49','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_9\\/4 9 2015 hc.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','1','','','1','63','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2089','Đêm hội trên cao nguyên Mộc Châu','dem-hoi-tren-cao-nguyen-moc-chau','','<p style=\"text-align: justify;\">Chương trình nghệ thuật đêm 1/9, chào mừng kỷ niệm 70 năm Cách mạng Tháng Tám và Quốc khách nước CHXHCN Việt Nam được huyện Mộc Châu tổ chức hoành tráng tại Sân vận động huyện với chủ đề \"Đêm hội Văn hóa các dân tộc Mộc Châu năm 2015\". Dự chương trình có đ/c Bùi Đức Hải - Phó chủ tịch UBND tỉnh  Sơn La, đ/c Nhâm Thị Phương - Tỉnh ủy viên - Bí thư huyện ủy cùng các đ/c trong Thường trực Huyện ủy, HĐND- UBND huyện Mộc Châu.</p>\r\n','\r\n<p style=\"text-align: justify;\">Chương trình nghệ thuật đêm 1/9, chào mừng kỷ niệm 70 năm Cách mạng Tháng Tám và Quốc khách nước CHXHCN Việt Nam được huyện Mộc Châu tổ chức hoành tráng tại Sân vận động huyện với chủ đề \"Đêm hội Văn hóa các dân tộc Mộc Châu năm 2015\". Dự chương trình có đ/c Bùi Đức Hải - Phó chủ tịch UBND tỉnh  Sơn La, đ/c Nhâm Thị Phương - Tỉnh ủy viên - Bí thư huyện ủy cùng các đ/c trong Thường trực Huyện ủy, HĐND - UBND huyện Mộc Châu.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi33.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Mở đầu chương trình là màn giới thiệu về miền đất, con người và bản sắc văn hóa các dân tộc huyên Mộc Châu, được dàn dựng công phu, hấp dẫn, trong đó, điểm nhấn là hình ảnh đẹp của cô gái Thái và chàng trai miền xuôi lên thăm cao nguyên, say sưa trong tình người Châu Mộc. Qua những tiết mục ca, múa các diễn viên đã khéo léo giới thiệu những nét văn hóa đặc sắc nhất trong đời sống sinh hoạt, lao động sản xuất; trong việc cưới hỏi, đón Tết của đồng bào các dân tộc Mộc Châu. Tiếng hát, tiếng khèn hòa trộn với âm hưởng rộn ràng của tiếng cồng, chiêng trong đêm hội đã mang đến cho khán giả những trải nghiệm vô cùng thú vị về mảnh đất, con người Mộc Châu.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi27.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Trong chương trình nghệ thuật, hấp dẫn và thu hút được sự tham gia của đông đảo bà con nhân dân cũng như du khách đó là vòng xòe với chủ đề<em> “Ấn tượng Mộc Châu”. </em>Ở đây, khán giả được hòa mình vào những điệu múa truyền thống đặc sắc đồng bào các dân tộc Mộc Châu như: điệu múa chuông của dân tộc Dao; điệu xòe của dân tộc Thái;  múa khèn, nhẩy Tha Kềnh của dân tộc Mông hay điệu múa Sạp dân tộc Mường. Tất cả hòa quyện với nhau tạo nên một bức tranh văn hóa phong phú, đa sắc màu, đầy hấp dẫn và mới lạ.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi37.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Đêm hội 1/9 kết thúc bằng màn bắn pháo bông và vòng xòe đoàn kết đã để lại nhiều ấn tượng sâu sắc trong lòng khán giả. Đây là một hoạt động văn hóa độc đáo mang ý nghĩa to lớn, thể hiện tinh thần đoàn kết, gắn bó của đồng bào các dân tộc  Mộc Châu. Vòng xòe đoàn kết ấy sẽ luôn mở rộng đón chào du khách đến với Mộc Châu, để cùng hòa mình vào không khí sôi động của lễ hội và cùng cảm nhận về mảnh đất cao nguyên tươi đẹp với những con người giản dị, mộc mạc, chân thành, giàu lòng mến khách.</p>\r\n<p style=\"text-align: justify;\"><em>Một số hình ảnh đêm hội</em>:</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi3.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi4.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi6.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi7.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi8.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi9.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi10.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi11.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi12.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi13.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi14.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi15.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi16.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi17.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 hoi18.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/4 9 2015 