insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2091','Đề cương tuyên truyền Kỷ niệm 120 năm ngày thành lập tỉnh Sơn La','de-cuong-tuyen-truyen-ky-niem-120-nam-ngay-thanh-lap-tinh-son-la','','<table style=\"width: 487px; height: 134px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"274\">\r\n<h1 align=\"center\"><span style=\"font-size: small;\">HUYỆN UỶ MỘC CHÂU</span></h1>\r\n<h1 align=\"center\"><span style=\"font-size: small;\">BAN TUYÊN GIÁO</span></h1>\r\n<p align=\"center\"><span style=\"font-size: small;\"><strong>*</strong></span></p>\r\n<h4 align=\"center\"><span style=\"font-size: small;\">Số 07- CV/BTGHU</span></h4>\r\n<p align=\"center\"><span style=\"font-size: small;\"><em>V/v gửi tài liệu tuyên truyền </em></span></p>\r\n</td>\r\n<td valign=\"top\" width=\"324\">\r\n<h3 align=\"center\"><span style=\"font-size: small;\">      ĐẢNG CỘNG SẢN VIỆT NAM</span></h3>\r\n<h1><span style=\"font-size: small;\"><em> </em></span></h1>\r\n<h1><span style=\"font-size: small;\"><em> </em></span></h1>\r\n<h1><span style=\"font-size: small;\"><em> </em></span></h1>\r\n<h1 style=\"text-align: center;\"><span style=\"font-size: small;\"><em>Mộc Châu, ngày 08 tháng 9 năm 2015 </em></span></h1>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: justify;\" align=\"center\"> </p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"174\">\r\n<p align=\"right\">           <strong><em>Kính gửi:   </em></strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"430\">\r\n<p><strong>- Các chi, đảng bộ cơ sở; </strong></p>\r\n<p><strong>- Mặt trận Tổ quốc và các tổ chức đoàn thể chính trị - xã hội huyện;</strong></p>\r\n<p><strong>- Các cơ quan trong khối tuyên truyền;</strong></p>\r\n<p><strong>- Các đồng chí Báo cáo viên cấp huyện.</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: justify;\"> </p>\r\n<p style=\"text-align: justify;\">Thực hiện Công văn số 2159-CV/BTGTU ngày 07.9.2015 của Ban Tuyên giáo tỉnh uỷ Sơn La, Ban Tuyên giáo huyện ủy kính gửi các chi, đảng bộ cơ sở; Mặt trận Tổ quốc và các tổ chức đoàn thể chính trị - xã hội huyện; các cơ quan trong khối tuyên truyền; các đồng chí Báo cáo viên cấp huyện Đề cương tuyên truyền Kỷ niệm 120 năm Ngày thành lập tỉnh Sơn La (10.10.1895 - 10.10.2015).<em> (có Đề cương chi tiết kèm theo: <a href=\"images/Anh_tin_bai_2015/Van_ban/%20CNG%20TUYN%20TRUYN%20THANH%20LAP%20TINH.doc\"><span style=\"font-size: small;\"><strong><span style=\"color: #0000ff;\">Tải về tại đây</span></strong></span></a>)</em></p>\r\n<p style=\"text-align: justify;\">Trên cơ sở nội dung Đề cương tuyên truyền, đề nghị các chi, đảng bộ cơ sở, cơ quan, đơn vị; các đồng chí Báo cáo viên cấp huyện tuyên truyền đến cán bộ, đảng viên và nhân dân./.</p>\r\n<p style=\"text-align: justify;\"> </p>\r\n<table style=\"width: 489px; height: 143px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"259\">\r\n<h2>Nơi nhận:</h2>\r\n<p> </p>\r\n<p>- Như kính gửi;</p>\r\n<p>- Thường trực huyện uỷ;</p>\r\n<p>- Lãnh đạo Ban;</p>\r\n<p>- Lưu BTGHU.</p>\r\n</td>\r\n<td valign=\"top\" width=\"366\">\r\n<h3 align=\"center\">TRƯỞNG BAN</h3>\r\n<p align=\"center\"><strong> </strong></p>\r\n<p align=\"center\">(đã ký)</p>\r\n<p align=\"center\"><strong> </strong></p>\r\n<h3 align=\"center\"> Phạm Thị Nhung</h3>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>','','1','0','0','13','2015-09-09 00:46:23','196','','2015-09-09 00:46:38','196','196','2015-09-09 00:46:29','2015-09-09 00:46:23','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','4','','','1','280','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','0','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2092','Khai giảng lớp bồi dưỡng lý luận chính trị và nghiệp vụ dành chio cán bộ MTTQVN ở cơ sở năm 2015','khai-giang-lop-boi-duong-ly-luan-chinh-tri-va-nghiep-vu-danh-chio-can-bo-mttqvn-o-co-so-nam-2015','','<p style=\"text-align: justify;\">Sáng 7/9, Trung tâm Bồi dưỡng Chính trị huyện Mộc Châu phối hợp với UBMTTQVN huyện tổ chức khai giảng lớp bồi dưỡng lý luận chính trị và nghiệp vụ dành cho cán bộ MTTQVN ở cơ sở 2015.</p>\r\n','\r\n<p style=\"text-align: justify;\">Sáng 7/9, Trung tâm Bồi dưỡng Chính trị huyện Mộc Châu phối hợp với UBMTTQVN huyện tổ chức khai giảng lớp bồi dưỡng lý luận chính trị và nghiệp vụ dành cho cán bộ MTTQVN ở cơ sở 2015. Dự lễ khai giảng có đồng chí Tăng Văn Xiến – UVBTV – Chủ tịch UBMTTQ Việt Nam huyện Mộc Châu, đ/c Nguyễn Đức Nguyên - Ủy viên BTV - Phó trưởng Ban Tuyên giáo huyện ủy.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/9 9 2015 mt.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Trong thời gian 5 ngày từ ngày 7/9 đến ngày 11/9/2015 các học viên là cán bộ MTTQVN ở cơ sở sẽ được tiếp thu những kiến thức cơ bản về chủ nghĩa Mác Lê nin tư tưởng Hồ Chí Minh, về Đảng Cộng sản việt Nam, hệ thống chính trị và phương thức lãnh đạo của Đảng đối với hệ thống chính trị ở nước ta hiện nay, tìm hiểu những vấn đề cơ bản về Mặt trận Tổ quốc Việt Nam cũng như tổ chức và hoạt động của Mặt trận Tổ quốc các cấp, từ đó vận dụng sáng tạo, hiệu quả vào thực hiện nhiệm vụ tại cơ sở.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/9 9 2015 mt1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Lớp bồi dưỡng nhằm trang bị những kiến thức, kỹ năng mới hết sức cơ bản, bổ ích và xác thực nhất giúp cho cán bộ Mặt trận cấp cơ sở phát huy tốt vai trò, trách nhiệm tổ chức, điều hành các hoạt động ở cơ sở trong thời gian tới, đặc biệt là trong công tác vận động quần chúng tham gia xây dựng nông thôn mới.</p>\r\n<p style=\"text-align: right;\"><strong>Thanh Bình</strong></p>','1','0','0','23','2015-09-09 01:31:32','196','','2015-09-09 01:31:56','196','0','0000-00-00 00:00:00','0000-00-00 00:00:00','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_9\\/9 9 2015 mt.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','7','','','1','24','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2093','Cơ quan chức năng bắt giữ xe khách vận chuyển trên 300Kg nội tạng bản','co-quan-chuc-nang-bat-giu-xe-khach-van-chuyen-tren-300kg-noi-tang-ban','','<p style=\"text-align: justify;\">Hồi 2h ngày 7/9 trên Quốc lộ 6 thuộc địa phận tiểu khu 7 thị trấn Mộc Châu, Đội Quản lý thị trường Số 3 huyện Mộc Châu phối hợp với Công an huyện Mộc Châu tiến hành kiểm tra xe khách Biển kiểm soát 27B 000.90 đi theo hướng Hưng Yên- Sơn La, do ông Vũ Văn Thái, hộ khẩu thường trú: Quang Khải, Tứ Kỳ, Hải Dương điều khiển. Qua kiểm tra phát hiện trong cốp xe có trên 300 kg nội tạng động vật đã bốc mùi hôi thối và hơn 3000 quả trứng không có giấy kiểm dịch.</p>\r\n','\r\n<p style=\"text-align: justify;\">Hồi 2h ngày 7/9 trên Quốc lộ 6 thuộc địa phận tiểu khu 7 thị trấn Mộc Châu, Đội Quản lý thị trường Số 3 huyện Mộc Châu phối hợp với Công an huyện Mộc Châu tiến hành kiểm tra xe khách Biển kiểm soát 27B 000.90 đi theo hướng Hưng Yên- Sơn La, do ông Vũ Văn Thái, hộ khẩu thường trú: Quang Khải, Tứ Kỳ, Hải Dương điều khiển. Qua kiểm tra phát hiện trong cốp xe có trên 300 kg nội tạng động vật đã bốc mùi hôi thối và hơn 3000 quả trứng không có giấy kiểm dịch.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/9 9 2015 kh.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">The lời khai ban đầu, lái xe Vũ Văn Thái đã mua số nội tạng trên tại các lò mổ động vật ở Hưng Yên, sau đó vận chuyển lên thành phố Sơn La bán lại cho các tiểu thương và nhà hàng. Do công tác bảo quản không đúng quy định nên số nội tạng trên bị hư hỏng.</p>\r\n<p style=\"text-align: justify;\">Đội Quản lý thị trường Số 3 huyện Mộc Châu đã ra quyết định xử phạt hành chính đối với ông Vũ Văn Thái với số tiền 3,5 triệu đồng, tịch thu toàn bộ số nội tạng trên và bàn giao cho cơ quan thú ý tiêu hủy, riêng 3000 quả trứng, cơ quan chức năng cũng đã yêu cầu chủ hàng kiểm dịch lại theo đúng quy định của pháp luật.</p>\r\n<p style=\"text-align: right;\"><strong>Đức Cường</strong></p>','1','0','0','18','2015-09-09 01:35:26','196','','2015-09-09 07:28:44','196','196','2015-09-09 07:28:33','2015-09-09 07:28:28','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_9\\/9 9 2015 kh.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','4','0','2','','','1','40','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2094','Các Đồng chí lãnh đạo Huyện dự khai giảng năm học mới 2015 - 2016','cac-dong-chi-lanh-dao-huyen-du-khai-giang-nam-hoc-moi-2015-2016','','<p style=\"text-align: justify;\">Hòa chung trong không khí tưng bừng của ngày hội toàn dân đưa trẻ đến trường. Ngày 5/9, trường Trung học cơ sở bán trú, trường tiểu học và mầm non xã Chiềng Khừa huyện Mộc Châu đã tổ chức Lễ Khai giảng năm học 2015 - 2016.</p>\r\n','\r\n<p style=\"text-align: justify;\">Hòa chung trong không khí tưng bừng của ngày hội toàn dân đưa trẻ đến trường. Ngày 5/9, trường Trung học cơ sở bán trú, trường tiểu học và mầm non xã Chiềng Khừa huyện Mộc Châu đã tổ chức Lễ Khai giảng năm học 2015 - 2016. Dự và phát biểu tại buổi Lễ có đồng chí Trần Anh Tuấn, Ủy viên Ban thường vụ Tỉnh ủy - Giám đốc Công an tỉnh, đồng chí Nhâm Thị Phương - Bí thư Huyện ủy - Chủ tịch HĐND huyện, cùng dự còn có đại diện Sở GD&amp;ĐT; cấp ủy, chính quyền địa phương và đông đảo phụ huynh học sinh trên địa bàn xã.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/9 9 2015 ta3.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Chiềng Khừa là xã vùng 3 biên giới đặc biệt khó khăn của huyện Mộc Châu, xã có 7 bản với 100% học sinh là con em đồng bào các dân tộc thiểu số. Trong những năm qua, các trường Mầm non, Tiểu học, PTDT bán trú, THCS xã Chiềng Khưa đã được Nhà nước quan tâm đầu tư hệ thống cơ sở hạ tầng, trường lớp học cơ bản đáp ứng nhu cầu dạy và học của con em trong xã. Đối với đội ngũ giáo viên, ngoài chế độ ưu đãi cho cán bộ giáo viên đối với vùng đặc biệt khó khăn, cơ sở vật chất trường lớp học, nhà công vụ giáo viên cũng được đầu tư xây dựng khang trang, tạo điều kiện để đội ngũ cán bộ, giáo viên yên tâm công tác.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/9 9 2015 ta.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Phát biểu tại Lễ Khai giảng, đồng chí Trần Anh Tuấn, Ủy viên Ban thường vụ Tỉnh ủy - Giám đốc Công an tỉnh ghi nhận và biểu dương những kết quả đã đạt được của các nhà trường trong năm học qua. Bước vào năm học 2015 - 2016 với kỳ vọng sẽ có nhiều đổi mới, đồng chí mong muốn: cán bộ, giáo viên các nhà trường sẽ tiếp tục nâng cao chất lượng dạy và học, tích cực đưa kỹ năng sống vào vào giảng dạy, giúp các em học sinh được phát triển toàn diện... Thay mặt lãnh đạo tỉnh, đồng chí đã trao tặng 3 bộ máy lọc nước cho các nhà trường và chúc các thầy cô giáo, các học sinh có sức khỏe dồi dào, ra sức thi đua dạy tốt, học tốt.</p>\r\n<p style=\"text-align: justify;\"> </p>\r\n<p style=\"text-align: justify;\">Dự lễ khai giảng tại Trường PTDT Bán trú THCS Lóng Sập có đ/c Hà Trung Chiến - Phó Bí thư huyện ủy - Chủ tịch UBND huyện cùng đoàn công tác của huyện. </p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/9 9 2015 ls.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Năm học 2014 - 2015 Trường PTDT Bán trú THCS Lóng Sập đã thực hiện tốt chương trình giáo dục và các hoạt động nâng cao chất lượng dạy và học; đổi mới đồng bộ phương pháp dạy học và giáo dục, đổi mới công tác kiểm tra đánh giá kết quả học tập và rèn luyện của học sinh; tổ chức hiệu quả các hoạt động ngoài giờ lên lớp theo chủ điểm, giáo dục truyền thống, xây dựng nếp sống văn hóa; củng cố kỷ cương, nề nếp; lồng ghép, rèn luyện kỹ năng sống, giáo dục đạo đức cho học sinh; đẩy mạnh ứng dụng CNTT trong quản lý và giảng dạy, phát huy tính tích cực, tự học, tự tìm tòi của người học, qua đó tạo sự chuyển biến tích cực, rõ nét về chất lượng và hiệu quả hoạt động ở nhà trường. Kết quả năm học vừa qua, nhà trường có  24,6% học sinh xếp loại học lực khá, giỏi, trên 89,7% đạt hạnh kiểm khá, tốt. Bên cạnh đó, nhà trường cũng tăng cường quan lý học sinh bán trú, xây dựng nội quy sinh hoạt, tổ chức tốt việc nấu ăn và chăm sóc sức khỏe cho học sinh. </p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/9 9 2015 ls1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Năm học 2015- 2016, Nhà trường tiếp tục thực hiện tốt các cuộc vận động, phong trào thi đua bằng những việc làm cụ thể, thiết thực, phù hợp với điều kiện của đơn vị gắn với đổi mới hoạt động giáo dục của nhà trường; xây dựng kế hoạch giáo dục theo định hướng phát triển năng lực học sinh thông qua việc điều chỉnh nội dung dạy học theo hướng tinh giản, đảm bảo chuẩn kiến thức; Đổi mới mạnh mẽ phương pháp dạy và học, nhằm phát huy tính tích cực, chủ động, sáng tạo của học sinh; tăng cường kỹ năng thực hành, vận dụng kiến thức, kỹ năng vào giải quyết các vấn đề thực tiễn.</p>\r\n<p style=\"text-align: justify;\">Lễ khai giảng Trường PTDT Bán trú THCS Lóng Sập diễn ra trong không khí trang trọng với phần lễ ngắn gọn, phần hội được tổ chức sôi động, với nhiều hoạt động hấp dẫn tạo không khí vui tươi, phấn khởi cho học sinh trong ngày khai giảng năm học mới.</p>\r\n<p style=\"text-align: justify;\">Cũng trong ngày, đồng chí Giàng Khánh Ly – Phó chủ tịch UBND huyện Mộc Châu đã đi dự lễ khai giảng cùng cô và trò trường tiểu học 15/10 – Thị trấn Nông trường Mộc Châu.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/9 9 2015 ly1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Bước vào năm học mới 2015-2016, Trường Tiểu học 15/10 có 260 học sinh ở các khối học, trong đó học sinh đầu cấp là 60 em. Với tinh thần đổi mới, tập trung vào học sinh, lễ khai giảng năm nay của trường Tiểu học 15/10 bắt đầu với thư chúc mừng năm học mới của chủ tịch nước Trường Tấn Sang nhân ngày hội khai trường. Tiếp đó là bài phát biểu ngắn gọn của thầy giáo Hiệu trưởng nhà trường với mục tiêu tạo động lực cho toàn trường bước vào năm học mới thay cho bài diễn văn diễn văn dài dòng như những năm trước. Sau bài phát biểu, các em học sinh đã được nghe tiếng trống khai trường và được hòa mình vào không khí của phần hội với nhiều tiết mục văn nghệ đặc sắc, sôi động có nội dung ca ngợi thầy cô, bạn bè và mái trường mến yêu.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/9 9 2015 ly.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Phát biểu tại lễ khai giảng, đồng chí Giàng Khánh Ly – Phó Chủ tịch UBND huyện cũng đã biểu dương những kết quả mà tập thể thầy và trò trường tiểu học 15/10 đã đạt được trong năm học vừa qua. Bước vào năm học mới, đồng chí yêu cầu nhà trường phát huy những kết quả đã đạt được, đoàn kết, nỗ lực khắc phục mọi khó khăn, quán triệt thực hiện các Chỉ thị, Nghị quyết Đại hội Đảng các cấp về nhiệm vụ, mục tiêu phát triển giáo dục đào tạo; tiếp tục đổi mới, nâng cao chất lượng giáo dục, quyết tâm thực hiện thắng lợi các mục tiêu mà nhà trường đặt ra trong năm học mới.</p>\r\n<p style=\"text-align: right;\"><strong>Nhóm PV Đài Mộc Châu</strong></p>','1','0','0','25','2015-09-09 07:24:54','196','','2015-09-09 07:26:55','196','196','2015-09-09 07:26:39','2015-09-09 07:24:54','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_9\\/9 9 2015 ly1.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','4','','','1','42','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2095','Hội nghị tham gia đóng góp ý kiến vào các văn bản trình kỳ họp chuyên đề lần thứ 6 HĐND tỉnh','hoi-nghi-tham-gia-dong-gop-y-kien-vao-cac-van-ban-trinh-ky-hop-chuyen-de-lan-thu-6-hdnd-tinh','','<p style=\"text-align: justify;\">Mới đây, HĐND tỉnh Sơn La phối hợp cùng Thường trực HĐND huyện Mộc Châu tổ chức lấy ý kiến tham gia đóng góp vào các văn bản trình Kỳ họp chuyên đề lần thứ 6 HĐND tỉnh khóa 13. Đồng chí Nhâm Thị Phương – Tỉnh ủy viên - Bí thư huyện ủy, Chủ tịch HĐND huyện, Tổ trưởng Tổ đại biểu HĐND tỉnh ứng cử  tại  Mộc Châu chủ trì hội nghị.</p>\r\n','\r\n<p style=\"text-align: justify;\">Mới đây, HĐND tỉnh Sơn La phối hợp cùng Thường trực HĐND huyện Mộc Châu tổ chức lấy ý kiến tham gia đóng góp vào các văn bản trình Kỳ họp chuyên đề lần thứ 6 HĐND tỉnh khóa 13. Đồng chí Nhâm Thị Phương – Tỉnh ủy viên - Bí thư huyện ủy, Chủ tịch HĐND huyện, Tổ trưởng Tổ đại biểu HĐND tỉnh ứng cử  tại  Mộc Châu chủ trì hội nghị.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/9 9 2015 h.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Tại hội nghị, các đại biểu đã nhất trí cao với nội dung dự thảo 8 Nghị quyết trình kỳ họp chuyên đề lần thứ 6 HĐND tỉnh khóa 13. Các đại biểu đã đóng góp bổ sung các ý kiến vào Đề án xây dựng nông thôn mới tỉnh Sơn La giai đoạn 2016 – 2020 như: Lộ trình thực hiện đề án; mức thu nhập của từng vùng; điều chỉnh tổng khái toán thực hiện Đề án của các xã; việc tổng kết thực hiện các chính sách; bổ sung hỗ trợ các xã biên giới. Về kế hoạch chuẩn bị đầu tư các dự án sử dụng vốn đầu tư công kế hoạch trung hạn 5 năm 2016-2020, các đại biểu đề nghị bổ sung về xây dựng công trình đường lên đỉnh Pha Luông; hạ tầng vùng nông nghiệp; Dự án kè suối Quanh, hồ Điều hòa tại thị trấn  Mộc Châu…</p>\r\n<p style=\"text-align: right;\"><strong>Thanh Bình</strong></p>','1','0','0','107','2015-09-09 07:37:00','196','','2015-09-09 07:42:15','196','196','2015-09-09 07:37:46','2015-09-09 07:37:00','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_9\\/9 9 2015 h.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','3','0','3','','','1','27','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2096','Huyện Mộc Châu tổ chức hội nghị lấy ý kiến góp ý vào  Dự thảo Bộ luật Hình sự (sửa đổi)','huyen-moc-chau-to-chuc-hoi-nghi-lay-y-kien-gop-y-vao-du-thao-bo-luat-hinh-su-sua-doi','','<p style=\"text-align: justify;\">Ngày 7/9, HĐND huyện Mộc Châu tổ chức hội nghị lấy ý kiến góp ý vào Dự thảo Bộ luật Hình sự (sửa đổi) trên địa bàn huyện Mộc Châu. Đ/c Nguyễn Thị Bích Hòa - Phó Chủ tịch HĐND huyện chủ trì hội nghị.</p>\r\n','\r\n<p style=\"text-align: justify;\">Ngày 7/9, HĐND huyện Mộc Châu tổ chức hội nghị lấy ý kiến góp ý vào Dự thảo Bộ luật Hình sự (sửa đổi) trên địa bàn huyện Mộc Châu. Đ/c Nguyễn Thị Bích Hòa - Phó Chủ tịch HĐND huyện chủ trì hội nghị.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/9 9 2015 hs.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Để tổ chức lấy ý kiến góp ý vào Dự thảo Bộ luật Hình sự (sửa đồi), Ngày 12/8/2015 Thường trực HĐND huyện ban hành kế hoạch số 37, đồng thời sao gửi các tài liệu liên quan về Dự thảo Bộ luật Hình sự (sửa đổi) cho các cơ quan, đơn vị, các xã, thị trấn làm cơ sở nghiên cứu, tham gia góp ý. Các hình thức lấy ý kiến được tổ chức phong phú, đa dạng như: góp ý trực tiếp bằng văn bản, qua hòm thư điện tử của Phòng Tư pháp và các phương tiện thông tin đại chúng; tổ chức hội nghị, hội thảo, tọa đàm… Về cơ bản, cán bộ công chức, viên chức, chiến sỹ các lực lượng vũ trang và nhân dân các dân tộc huyện Mộc Châu nhất trí cao với Dự thảo Bộ luật Hình sự (sửa đổi). Tuy nhiên, cũng đã có 9586 lượt người tham gia ý kiến sửa đổi, bổ sung về: Nội dung thuộc phần chung; bố cục, vị trí của các chương, điều, khoản; vấn đề trách nhiệm hình sự của pháp nhân và loại tội pháp nhân chịu trách nhiệm hình sự; việc bỏ hình phạt tử hình ở một số tội; bãi bỏ một số tội phạm và bổ sung một số tội phạm mới, nhằm bảo vệ quyền con người, quyền công dân, bảo vệ, thúc đẩy nền kinh tế thị trường định hướng xã hội chủ nghĩa và thực hiện chủ trương hội nhập quốc tế…</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/9 9 2015 hs1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Việc lấy ý kiến đóng góp của nhân dân vào Dự thảo Bộ luật Hình sự (sửa đổi) là công việc có ý nghĩa hết sức quan trọng, nhằm phát huy quyền làm chủ, huy động trí tuệ, tâm huyết của nhân dân trong việc tham gia đóng góp ý kiến vào dự thảo Bộ luật Hình sự (sửa đổi). Qua đó góp phần nâng cao nhận thức, trách nhiệm của các ngành, các cấp trên địa bàn huyện đối với việc sửa đổi và thực hiện Bộ luật Hình sự.</p>\r\n<p style=\"text-align: right;\"><strong>Thùy Mai</strong></p>','1','0','0','107','2015-09-09 07:47:06','196','','2015-09-09 07:47:35','196','0','0000-00-00 00:00:00','2015-09-09 07:47:06','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_9\\/9 9 2015 hs.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','3','0','2','','','1','28','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2097','Huyện ủy Mộc Châu tổng kết công tác chuẩn bị và tổ chức Đại hội Đảng  các cấp và Đại hội Đảng bộ huyện khóa XXI nhiệm kỳ 2015- 2020','huyen-uy-moc-chau-tong-ket-cong-tac-chuan-bi-va-to-chuc-dai-hoi-dang-cac-cap-va-dai-hoi-dang-bo-huyen-khoa-xxi-nhiem-ky-2015-2020','','<p style=\"text-align: justify;\">Trong tuần qua, Ban Thường vụ huyện ủy Mộc Châu tổ chức hội nghị tổng kết công tác chuẩn bị và tổ chức Đại hội Đảng các cấp, Đại hội Đảng bộ huyện Mộc Châu khóa XXI nhiệm kỳ 2015- 2020. Đ/c Nhâm Thị Phương - Tỉnh ủy viên - Bí thư huyện ủy chủ trì hội nghị.</p>\r\n','\r\n<p style=\"text-align: justify;\">Trong tuần qua, Ban Thường vụ huyện ủy Mộc Châu tổ chức hội nghị tổng kết công tác chuẩn bị và tổ chức Đại hội Đảng các cấp, Đại hội Đảng bộ huyện Mộc Châu khóa XXI nhiệm kỳ 2015- 2020. Đ/c Nhâm Thị Phương - Tỉnh ủy viên - Bí thư huyện ủy chủ trì hội nghị.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/9 9 2015 tk.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Để thực hiện tốt công tác chuẩn bị và tổ chức Đại hội Đảng các cấp và Đại hội Đảng bộ huyện Mộc Châu khóa XXI nhiệm kỳ 2015- 2020, BTV huyện ủy Mộc Châu đã tổ chức quán triệt Chỉ thị 36 CT/TW của Bộ chính trị cùng các văn bản hướng dẫn của TW và Tỉnh ủy Sơn La về công tác triển khai Đại hội Đảng các cấp, tiến tới Đại hội Đại biểu toàn quốc lần thứ XII của Đảng. Ban chấp hành, BTV huyện ủy đã ban hành 258 văn bản các loại để lãnh đạo, chỉ đạo thực hiện công tác chuẩn bị và tổ chức Đại hội Đảng bộ các cấp. Đến hết ngày 30/5/2015, 100% các chi, đảng bộ cơ sở trực thuộc đã tổ chức thành công Đại hội nhiệm kỳ 2015-2020.</p>\r\n<p style=\"text-align: justify;\">Công tác chuẩn bị Đại hội Đảng bộ huyện khóa XXI được tiến hành chu đáo, kỹ lưỡng đảm bảo tiến độ đề ra. Trong đó công tác tuyên truyền được triển khai bằng nhiều hình thức phong phú thiết thực, sâu rộng, hiệu quả; công tác xây dựng văn kiện được chuẩn bị bài bản, khoa học, công phu từ nội dung đến hình thức; việc tổ chức lấy ý kiến đóng góp vào văn kiện Đại hội Đảng các cấp được thực hiện nghiêm túc, chất lượng; công tác nhân sự đúng hướng dẫn quy trình, dân chủ công khai, đảm bảo về tiêu chuẩn, cơ cấu, trình độ, độ tuổi.</p>\r\n<p style=\"text-align: justify;\">Song song với công tác chuẩn bị các điều kiện cho Đại hội, Đảng bộ, chính quyền và nhân dân các dân tộc huyện Mộc Châu tích cực đẩy mạnh các phong trào thi đua yêu nước với những việc làm thiết thực, tạo tiền đề, động lực để cán bộ, đảng viên và nhân dân thực hiện thắng lợi nhiệm vụ phát triển kinh tế - xã hội năm 2015 và những năm tiếp theo. Đặc biệt là huyện Mộc Châu đẩy nhanh tiến độ các công trình trọng điểm và tổ chức khánh thành các công trình chào mừng Đại hội Đảng bộ huyện Mộc Châu nhằm tạo khí thế thi đua sôi nổi lan toả từ huyện đến cơ sở và từng người dân cũng như đáp ứng được nhu cầu, nguyện vọng chính đáng của nhân dân.   </p>\r\n<p style=\"text-align: justify;\">Đại hội Đảng bộ huyện Mộc Châu được tiến hành từ ngày 24 đến ngày 26/7/2015 đã thành công tốt đẹp. Đại hội Đảng bộ huyện đã bầu 39 đồng chí vào Ban Chấp hành Đảng bộ khóa mới và bầu đoàn đại biểu đi dự Đại hội Đảng bộ tỉnh Sơn La lần thứ XIV gồm 24 đại biểu chính thức và 3 đại biểu dự khuyết. </p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/9 9 2015 tk1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Tại hội nghị, 24 tập thể và 40 cá nhân có thành tích xuất sắc trong công tác chuẩn bị và tổ chức Đại hội Đảng các cấp và Đại hội Đảng bộ huyện Mộc Châu khóa XXI nhiệm kỳ 2015- 2020 đã được BTV huyện ủy tặng giấy khen.</p>\r\n<p style=\"text-align: right;\"><strong>Thùy Mai</strong></p>','1','0','0','23','2015-09-09 07:54:55','196','','2015-09-09 07:55:52','196','0','0000-00-00 00:00:00','2015-09-09 07:54:55','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_9\\/9 9 2015 tk.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','3','0','6','','','1','36','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2098','BIỂU TỔNG HỢP ĐƠN KHIẾU NẠI','bieu-tong-hop-don-khieu-nai','','<p style=\"text-align: center;\"><strong>BIỂU TỔNG HỢP ĐƠN KHIẾU NẠI</strong></p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p style=\"text-align: center;\"><strong>Biểu tổng hợp đơn khiếu nại</strong></p>\r\n<p style=\"text-align: center;\">Tính từ ngày 15/8/2014 đến ngày 31/7/2015</p>\r\n</td>\r\n<td style=\"text-align: center;\"><a href=\"images/Anh_tin_bai_2015/Van_ban/KN.pdf\"><span style=\"color: #0000ff; font-size: small;\"><strong>Xem chi tiết tại đây</strong></span></a></td>\r\n</tr>\r\n<tr>\r\n<td> </td>\r\n<td> </td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: left;\"><strong><em> </em></strong></p>\r\n<p style=\"text-align: left;\"><strong><em> </em></strong></p>','','1','0','0','144','2015-09-10 01:12:32','196','','2015-09-10 02:06:37','196','0','0000-00-00 00:00:00','2015-09-10 01:12:32','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','7','0','2','','','1','233','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','0','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2099','Hội nghị lần thứ 3 Ban Chấp hành Đảng bộ huyện Mộc Châu  khóa XXI nhiệm kỳ 2015- 2020','hoi-nghi-lan-thu-3-ban-chap-hanh-dang-bo-huyen-moc-chau-khoa-xxi-nhiem-ky-2015-2020','','<p style=\"text-align: justify;\">Ngày 8/9, Ban chấp hành Đảng bộ huyện Mộc Châu khóa XXI đã tổ chức hội nghị lần thứ 3 (lần 1). Đ/c Nhâm Thị Phương- Tỉnh ủy viên- Bí thư huyện ủy chủ trì hội nghị.</p>\r\n','\r\n<p style=\"text-align: justify;\">Ngày 8/9, Ban chấp hành Đảng bộ huyện Mộc Châu khóa XXI đã tổ chức hội nghị lần thứ 3 (lần 1). Đ/c Nhâm Thị Phương- Tỉnh ủy viên- Bí thư huyện ủy chủ trì hội nghị.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/10 9 2015 bch.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Tại hội nghị, các đại biểu đã được thông qua các Dự thảo: Quy chế làm việc của Ban chấp hành Đảng bộ huyện khóa XXI, nhiệm kỳ 2015- 2020; Quyết định của BCH Đảng bộ huyện về việc phân công nhiệm vụ và về nhiệm vụ, quyền hạn của các đồng chí Ủy viên Ban thường vụ, Ủy viên BCH Đảng bộ theo dõi phụ trách chi, đảng bộ cơ sở; Kế hoạch của BCH Đảng bộ xây dựng các chương trình hành động thực hiện Nghị quyết Đại hội Đảng bộ huyện khóa XXI; Báo cáo tổng hợp ý kiến tham gia Dự thảo văn kiện trình Đại hội lần thứ XII của Đảng; Báo cáo tổng hợp ý kiến tham gia vào Dự thảo Báo cáo chính trị trình Đại hội Đảng bộ tỉnh Sơn La lần thứ XIV và Đề án, tiêu chuẩn cơ cấu Ban chấp hành Đảng bộ tỉnh khóa XIV, nhiệm kỳ 2015- 2020. </p>\r\n<p style=\"text-align: justify;\">Kết luận hội nghị, đồng chí Nhâm Thị Phương- Tỉnh ủy viên- Bí thư huyện ủy đã đề nghị các đồng trong Ban chấp hành Đảng bộ huyện tập trung lãnh đạo, chỉ đạo các chi, đảng bộ xây dựng, ban hành quy chế làm việc, chương trình công tác, kiểm tra giám sát toàn khóa… tập trung tuyên truyền kết quả Đại hội Đảng bộ huyện Mộc Châu khóa XXI, nhiệm kỳ 2015- 2020; đẩy mạnh các phong trào thi đua yêu nước lập thành tích chào mừng Đại hội Đảng bộ tỉnh Sơn La lần thứ XIV, Đại hội Đảng toàn quốc lần thứ XII và kỷ niệm 120 năm ngày thành lập tỉnh Sơn La...</p>\r\n<p style=\"text-align: right;\"><strong>Thùy Mai</strong></p>','1','0','0','23','2015-09-11 01:02:45','196','','2015-09-11 01:03:10','196','0','0000-00-00 00:00:00','2015-09-11 01:02:45','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_9\\/10 9 2015 bch.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','5','','','1','39','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
