insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2101','BIỂU TỔNG HỢP ĐƠN ĐỀ NGHỊ, KIẾN NGHỊ, PHẢN ÁNH','bieu-tong-hop-don-de-nghi-kien-nghi-phan-anh','','<p style=\"text-align: center;\"><strong>BIỂU TỔNG HỢP ĐƠN ĐỀ NGHỊ, KIẾN NGHỊ, PHẢN ÁNH</strong></p>\r\n<table style=\"width: 485px; height: 96px;\" border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p style=\"text-align: center;\"><strong>Biểu tổng hợp đơn đề nghị, kiến nghị, phản ánh</strong></p>\r\n<p style=\"text-align: center;\">Tính từ ngày 01/8/2015 đến ngày 31/8/2015</p>\r\n</td>\r\n<td style=\"text-align: center;\"><a href=\"images/Anh_tin_bai_2015/Van_ban/BTH.pdf\"><span style=\"font-size: small; color: #0000ff;\"><strong>Xem chi tiết tại đây</strong></span></a></td>\r\n</tr>\r\n<tr>\r\n<td> </td>\r\n<td> </td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: left;\"><strong> </strong></p>','','1','0','0','144','2015-09-10 01:36:02','196','','2015-09-10 01:57:47','196','0','0000-00-00 00:00:00','2015-09-10 01:36:02','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','5','0','1','','','1','248','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','0','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2102','Phiên họp thứ 22 Ủy ban nhân dân huyện Mộc Châu  khóa XIX nhiệm kỳ 2011- 2016','phien-hop-thu-22-uy-ban-nhan-dan-huyen-moc-chau-khoa-xix-nhiem-ky-2011-2016','','<p style=\"text-align: justify;\">Ngày 8/9, UBND huyện Mộc Châu khóa XIX nhiệm kỳ 2011- 2016 họp phiên thứ 22, đánh giá tình hình phát triển kinh tế- xã hội, đảm bảo quốc phòng- an ninh tháng 8, đề ra nhiệm vụ trọng tâm công tác điều hành tháng 9/2015. Đ/c Hà Trung Chiến- Phó Bí thư Huyện ủy- Chủ tịch UBND huyện chủ trì phiên họp.</p>\r\n','\r\n<p style=\"text-align: justify;\">Ngày 8/9, UBND huyện Mộc Châu khóa XIX nhiệm kỳ 2011- 2016 họp phiên thứ 22, đánh giá tình hình phát triển kinh tế- xã hội, đảm bảo quốc phòng- an ninh tháng 8, đề ra nhiệm vụ trọng tâm công tác điều hành tháng 9/2015. Đ/c Hà Trung Chiến- Phó Bí thư Huyện ủy- Chủ tịch UBND huyện chủ trì phiên họp.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/10 9 2015 hop.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Trong tháng 8, UBND huyện đã tập trung chỉ đạo các xã, thị trấn khẩn trương khắc phục diện tích lúa, hoa màu bị ngập úng do ảnh hưởng của áp thấp, đảm  bảo sản xuất vụ mùa theo kế hoạch. Trong chăn nuôi, đôn đốc các xã, thị trấn triển khai thực hiện chính sách phát triển chăn nuôi đại gia súc, tăng cường công tác phòng, chống dịch bệnh, trồng được 3,96 ha cỏ. Lĩnh vực tài nguyên và môi trường, tập trung giải quyết các vụ việc còn tồn đọng về đất đai theo kiểm điểm Nghị quyết Trung Ương 4, đồng thời tiếp nhận, cấp giấy CNQSD đất cho các hộ trên địa bàn huyện, trong tháng đã cấp giấy chứng nhận QSD đất cho 92 hộ. Bên cạnh đó, UBND huyện cũng đôn đốc  quyết toán dự án hoàn thành theo Chỉ thị 25 của Thủ tướng Chính phủ, đã quyết toán được 17 dự án với tổng mức đầu tư trên 7 tỷ đổng. Thu ngân sách trên địa bàn đạt trên 68 tỷ đồng, bằng 117,74% dự toán UBND tỉnh giao, 90,87% dự toán HĐND huyện giao. Trong tháng 8, lĩnh vực du lịch tiếp tục có kết quả tốt, lượng khách đến tham quan, du lịch ước đạt 100.000 lượt, doanh thu ước đạt 73 tỷ đồng. Lĩnh vực giáo dục, y tế, văn hóa, xã hội tiếp tục được quan tâm, UBND huyện tập trung chỉ đạo phòng GD&amp;ĐT, các đơn vị trường học chuẩn bị  tốt các điều kiện cho năm học mới 2015- 2016 và tổ chức Lễ khai giảng năm học mới; các cơ quan văn hóa chuẩn bị và tổ chức thành công Ngày hội văn hóa các dân tộc Mộc Châu năm 2015, để lại ấn tượng tốt đẹp, giới thiệu được những nét đặc sắc nhất về miền đất, con người Mộc Châu thân thiện, hiếu khách, đậm đà bản sắc văn hóa dân tộc. Công tác quốc phòng- an ninh trước, trong và sau Ngày hội được quan tâm thực hiện, an ninh trật tự được giữ vững, an toàn giao thông được đảm bảo, đặc biệt tình hình khu vực cửa khẩu Quốc gia Lóng Sập ổn định, tạo không khí vui tươi cho nhân dân 2 bên biên giới Việt- Lào cùng vui đón Tết Độc lập của Việt Nam.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/10 9 2015 hop1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Tháng 9/2015, UBND huyện Mộc Châu tiếp tục chỉ đạo các xã, thị trấn hướng dẫn nông dân hoàn thành thu hoạch ngô vụ hè thu và chuẩn bị đất, giống, vật tư gieo trồng vụ thu đông theo kế hoạch; dôn đốc đẩy nhanh tiến độ trồng rừng năm 2015, phấn đấu hoàn thành trước ngày 20/9/2015; tập trung xây dựng kế hoạch phát triển kinh tế- xã hội và dự toán ngân sách năm 2016, xác định rõ nhiệm vụ trọng tâm, khâu đột phá của năm; tiếp tục giải quyết các vướng mắc, tồn tại về đất đai, hoàn thành việc cấp giấy chứng nhận quyền sử dụng đất cho các hộ khu hồ 70, thị trấn Nông Trường, khu quy hoạch dân cư km 73, tiểu khu 14, thị trấn Mộc Châu…</p>\r\n<p style=\"text-align: right;\"><strong>Thùy Mai</strong></p>','1','0','0','107','2015-09-11 01:06:59','196','','2015-09-11 01:07:24','196','0','0000-00-00 00:00:00','2015-09-11 01:06:59','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_9\\/10 9 2015 hop.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','1','','','1','49','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2103','Chuyển giao kiến thức và trải nghiệm canh tác  giống ngô biến đổi gen Dekalb tại Mộc Châu','chuyen-giao-kien-thuc-va-trai-nghiem-canh-tac-giong-ngo-bien-doi-gen-dekalb-tai-moc-chau','','<p style=\"text-align: justify;\">Ngày 8/9, Công ty Dekalb Việt Nam (thuộc Tập đoàn Monsanto- Mỹ) tổ chức chương trình “Chuyển giao kiến thức và trải nghiệm canh tác giống ngô biến đổi gen Dekalb” tại xã Chiềng Sơn, huyện Mộc Châu.</p>\r\n','\r\n<p style=\"text-align: justify;\">Ngày 8/9, Công ty Dekalb Việt Nam (thuộc Tập đoàn Monsanto- Mỹ) tổ chức chương trình “Chuyển giao kiến thức và trải nghiệm canh tác giống ngô biến đổi gen Dekalb” tại xã Chiềng Sơn, huyện Mộc Châu. Tham dự chương trình có Đ/c Hà Quyết Nghị - Tỉnh uỷ viên- Giám đốc Sở Nông nghiệp và PTNT tỉnh Sơn La, lãnh đạo phòng Nông nghiệp và đông đảo bà con nông dân huyện Mộc Châu.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/11 9 2015 ng.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Sau khi thăm quan thực tế và so sánh giữa ruộng ngô biến đổi gen với ruộng ngô thường cho thấy: Ruộng ngô sử dụng giống biến đổi gen Dekalb có thân cây cao, bắp to, hạt đều, khả năng chống chịu sâu bệnh tốt hơn so với giống ngô thường; tình trạng sâu đục thân, sâu đục bắp và sâu khoang cũng giảm rõ rệt so với các ruộng ngô thường.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/11 9 2015 ng2.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Theo tính toán của các chuyên gia nông nghiệp, trồng ngô biến đổi gen giúp giảm chi phí sản xuất tới gần 3 triệu đồng/1ha so với ngô thường vì chỉ phải phun thuốc diệt cỏ 1 lần và không phải phun thuốc trừ sâu trong suốt thời gian trồng. Thành công bước đầu của giống ngô này cũng là một tín hiệu vui cho người nông dân trong việc tiết kiệm chi phí sản xuất và tạo ra sản phẩm an toàn. Hiện Công ty DeKalb đang triển khai trồng thử nghiệm giống ngô này trên phạm vi toàn quốc, từ đó tính toán giá trị mà giống ngô biến đổi gen đem lại để làm cơ sở xác định giá bán của ngô giống.</p>\r\n<p style=\"text-align: right;\"><strong>Anh Đức</strong></p>','1','0','0','26','2015-09-11 07:41:01','196','','2015-09-11 08:33:46','196','0','0000-00-00 00:00:00','2015-09-11 08:33:46','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_9\\/11 9 2015 ng.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','3','0','1','','','1','26','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2104','Trên 300 phụ nữ xã Chiềng Sơn được truyền thông nội dung đề án 704 giáo dục 5 triệu bà mẹ nuôi dạy con tốt','tren-300-phu-nu-xa-chieng-son-duoc-truyen-thong-noi-dung-de-an-704-giao-duc-5-trieu-ba-me-nuoi-day-con-tot','','<p style=\"text-align: justify;\">Ngày 10/9, tại xã Chiềng Sơn, Hội LHPN huyện Mộc Châu tổ chức buổi truyền thông Đề án 704 “giáo dục 5 triệu bà mẹ nuôi dạy con tốt” cho trên 300 chị em Hội LHPH xã Chiềng Sơn.</p>\r\n','\r\n<p style=\"text-align: justify;\">Ngày 10/9, tại xã Chiềng Sơn, Hội LHPN huyện Mộc Châu tổ chức buổi truyền thông Đề án 704 “giáo dục 5 triệu bà mẹ nuôi dạy con tốt” cho trên 300 chị em Hội LHPH xã Chiềng Sơn.</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/11 9 2015 pn.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Tại buổi truyền thông các cán bộ hội viên phụ nữ được trực tiếp truyền đạt, trao đổi những nội dung cơ bản về: Vai trò của cha mẹ trong các giai đoạn phát triển của trẻ, ích lợi của việc nuôi con bằng sữa mẹ. Trong đó, tập trung vào việc nắm bắt tâm sinh lý của trẻ ở từng lứa tuổi; lựa chọn cách chăm sóc dinh dưỡng cho trẻ, giúp trẻ có diều kiện phát triển toàn diện cả thể lực và trí lực; biết cách tổ chức các hoạt động giải trí, vui chơi cho trẻ…</p>\r\n<p style=\"text-align: justify;\"><img src=\"images/Anh_tin_bai_2015/Thang_9/11 9 2015 pn1.jpg\" border=\"0\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">Bên cạnh đó, để triển khai tốt Đề án 704 “giáo dục 5 triệu bà mẹ nuôi dạy con tốt”, Hội LHPN huyện đã chỉ đạo, hướng dẫn tuyên truyên, vận động xây dựng các Câu lạc bộ xây dựng gia đình hạnh phúc, CLB nuôi dạy con tốt, CLB phụ nữ không có chồng, con, người thân nghiện ma túy; thực hiện xây dựng gia đình “5 không, 3 sạch” gắn với cuộc vận động “Toàn dân đoàn kết xây dựng đời sống văn hóa ở khu dân cư”. </p>\r\n<p style=\"text-align: justify;\">Thông qua các hình thức sinh hoạt câu lạc bộ, các buổi truyền thông cộng đồng nhằm cung cấp kiến thức và trao đổi kinh nghiệm cho chị em phụ nữ về cách chăm sóc, nuôi dạy con phù hợp theo độ tuổi của trẻ, giúp các gia đình nắm chắc được những kiến thức cơ bản về nuôi dạy con, quan tâm đến con em mình hơn, có ý thức trách nhiệm hơn trong việc chăm sóc và nuôi dạy con, từ đó thiết thực góp phần xây dựng gia đình “No ấm, tiến bộ, hạnh phúc”.</p>\r\n<p style=\"text-align: right;\"><strong>Đức Cường</strong></p>','1','0','0','25','2015-09-11 07:44:35','196','','2015-09-11 07:44:50','196','0','0000-00-00 00:00:00','0000-00-00 00:00:00','0000-00-00 00:00:00','{\"image_intro\":\"images\\/Anh_tin_bai_2015\\/Thang_9\\/11 9 2015 pn.jpg\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','3','','','1','28','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','1','*','');
