insert into `mc_content` ( `asset_id`,  `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) values('2106','GM  Dự Kỳ họp chuyên đề lần thứ 2 của HĐND huyện Mộc Châu  khoá XIX, nhiệm kỳ 2011 - 2016','gm-du-ky-hop-chuyen-de-lan-thu-2-cua-hdnd-huyen-moc-chau-khoa-xix-nhiem-ky-2011-2016','','<table style=\"width: 481px; height: 133px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"215\">\r\n<p align=\"center\"><strong>HỘI ĐỒNG NHÂN DÂN</strong></p>\r\n<p align=\"center\"><strong>HUYỆN MỘC CHÂU</strong></p>\r\n<p align=\"center\"><strong>_____________</strong></p>\r\n<p align=\"center\"><strong> </strong></p>\r\n<p>      Số: <strong>61</strong>/GM-HĐND</p>\r\n<p align=\"center\"><strong> </strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"406\">\r\n<p align=\"center\"><strong>CỘNG HOÀ XÃ HỘI CHỦ NGHĨA VIỆT NAM</strong></p>\r\n<p align=\"center\"><strong>Độc lập - Tự do - Hạnh phúc</strong></p>\r\n<p align=\"center\"><strong>_________________</strong></p>\r\n<p align=\"center\"><strong> </strong></p>\r\n<p align=\"center\"><em>Mộc Châu, ngày 14 tháng 9  năm 2015</em></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: justify;\"> </p>\r\n<p style=\"text-align: center;\" align=\"center\"><strong>GIẤY MỜI</strong></p>\r\n<p style=\"text-align: center;\" align=\"center\"><strong> Dự Kỳ họp chuyên đề lần thứ 2 của HĐND huyện Mộc Châu</strong></p>\r\n<p style=\"text-align: center;\" align=\"center\"><strong> khoá XIX, nhiệm kỳ 2011 - 2016 </strong></p>\r\n<p style=\"text-align: center;\" align=\"center\"><strong>____________________________</strong></p>\r\n<p style=\"text-align: justify;\">                        </p>\r\n<p style=\"text-align: justify;\">          Kính gửi: ……………………………………………………………..</p>\r\n<p style=\"text-align: justify;\"> </p>\r\n<p style=\"text-align: justify;\">Căn cứ Luật Tổ chức HĐND và UBND ngày 26/11/2003; Nghị quyết số 753/2005/NQ-UBTVQH11 ngày 02/4/2005 của Uỷ ban Thường vụ Quốc hội khoá XI về Ban hành Quy chế hoạt động của Hội đồng nhân dân;</p>\r\n<p style=\"text-align: justify;\">Hội đồng nhân dân huyện Mộc Châu tổ chức Kỳ họp chuyên đề lần thứ 2, HĐND huyện, Khoá XIX, nhiệm kỳ 2011 - 2016,</p>\r\n<p style=\"text-align: justify;\"><strong>1. Nội dung: </strong>Kỳ họp chuyên đề lần thứ 2 của HĐND huyện tập trung vào việc kiện toàn các chức danh Thường trực HĐND, các Ban của HĐND, Thư ký HĐND huyện và Lãnh đạo UBND huyện; xem xét danh mục dự án đầu tư xây dựng nông thôn mới xã Mường Sang năm 2016.</p>\r\n<p style=\"text-align: justify;\"><strong>2. Thời gian:</strong> 14h00’ ngày 17/9/2015.</p>\r\n<p style=\"text-align: justify;\"><strong>3. Địa điểm: </strong>Hội trường trung tâm hành chính huyện.</p>\r\n<p style=\"text-align: justify;\"><strong><em>Lưu ý:</em></strong><em> Thường trực HĐND các xã, thị trấn lập danh sách đại biểu tham dự kỳ họp gửi về Thường trực HĐND huyện qua Văn phòng HĐND&amp;UBND huyện <strong>trước 15 giờ ngày 16/9/2015</strong> để tổng hợp bố trí ăn, nghỉ (Liên hệ trực tiếp đồng chí Hà Văn Cường ĐT 0936.402.588 hoặc đồng chí Đinh Như Hùng ĐT 0945.598.889)</em></p>\r\n<p style=\"text-align: justify;\">Hội đồng nhân dân huyện đề nghị các vị đại biểu tới dự Kỳ họp chuyên đề lần thức 2, HĐND huyện, khoá XIX, nhiệm kỳ 2011 - 2016 đúng thành phần, thời gian quy định./.</p>\r\n<p style=\"text-align: justify;\"> </p>\r\n<table style=\"width: 480px; height: 95px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"310\">\r\n<p><strong><em>  Nơi nhận:</em></strong><strong>      </strong></p>\r\n<p>- Như thành phần;</p>\r\n<p>- Lưu: VT, Hùng, 100 bản.</p>\r\n</td>\r\n<td valign=\"top\" width=\"295\">\r\n<p align=\"center\"><strong>TM. THƯỜNG TRỰC HĐND</strong></p>\r\n<p align=\"center\"><strong>KT. CHỦ TỊCH</strong></p>\r\n<p align=\"center\"><strong>PHÓ CHỦ TỊCH</strong></p>\r\n<p align=\"center\"><strong> </strong></p>\r\n<p align=\"center\"><strong> </strong></p>\r\n<p align=\"center\"><strong>Nguyễn Thị Bích Hòa</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>','','1','0','0','101','2015-09-14 11:30:21','196','','2015-09-14 11:30:55','196','0','0000-00-00 00:00:00','2015-09-14 11:30:21','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}','2','0','2','','','1','25','{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}','0','*','');
